INSERT INTO `knowledge` (`id`, `name`, `content`, `image`, `knowledge_date`) VALUES
(1, 'Điều trị ung thư bằng Nấm Linh Chi đỏ', '<p><strong>Ung thư</strong>&nbsp;hay c&ograve;n gọi l&agrave; khối u &aacute;c t&iacute;nh - l&agrave; nh&oacute;m c&aacute;c bệnh li&ecirc;n quan đến sự thay đổi về sinh sản, tăng trưởng v&agrave; chức năng của tế b&agrave;o trong cơ thể người. C&aacute;c tế b&agrave;o b&igrave;nh thường bị đột biến v&agrave; tăng trưởng một c&aacute;ch mất kiểm so&aacute;t, c&aacute;c tế b&agrave;o đ&oacute; x&acirc;m lấn v&agrave;o c&aacute;c m&ocirc; ở gần hay di căn qua hệ thống bạch huyết hay mạch m&aacute;u. Di căn l&agrave; nguy&ecirc;n nh&acirc;n ch&iacute;nh g&acirc;y n&ecirc;n g&acirc;y tử vong cho người bệnh.</p>\r\n\r\n<p>Ung thư l&agrave; tập hợp nhiều bệnh chứ kh&ocirc;ng phải l&agrave; một bệnh. C&oacute; rất nhiều loại ung thư như : ung thư n&atilde;o, ung thư v&ograve;m họng, ung thư dạ d&agrave;y, ung thư gan, phổi, đjai tr&agrave;ng, tử cung... với v&ocirc; v&agrave;n những diễn biến kh&aacute;c nhau.</p>\r\n\r\n<p><strong>C&aacute;c nguy&ecirc;n nh&acirc;n g&acirc;y ung thư thường gặp, bao gồm:</strong></p>\r\n\r\n<ul>\r\n	<li>C&aacute;c t&aacute;c nh&acirc;n vật l&yacute; như: tia cực t&iacute;m v&agrave; bức xạ ion h&oacute;a;</li>\r\n	<li>C&aacute;c t&aacute;c nh&acirc;n h&oacute;a học như: a-mi-ăng (asbestos), c&aacute;c th&agrave;nh phần trong kh&oacute;i thuốc l&aacute;, aflatoxin (một chất nhiễm bẩn thức ăn), v&agrave; arsenic (một chất nhiễm bẩn nước uống)</li>\r\n	<li>C&aacute;c t&aacute;c nh&acirc;n sinh học: như nhiễm tr&ugrave;ng một số virus, vi khuẩn hay k&yacute; sinh tr&ugrave;ng&hellip;</li>\r\n</ul>\r\n\r\n<p><strong>Triệu chứng của ung thư v&agrave; c&aacute;ch hỗ trợ điều trị ung thư bằng Nấm Linh Chi đỏ</strong></p>\r\n\r\n<p>Phụ thuộc v&agrave;o từng vị tr&iacute; chứa tế b&agrave;o ung thư, đặc điểm, giai đoạn, khả năng di căn của khối u m&agrave; sẽ g&acirc;y ra những triệu chứng kh&aacute;c nhau. Việc chẩn đo&aacute;n bệnh ung thư thường đ&ograve;i hỏi phải sinh thiết rồi quan s&aacute;t tr&ecirc;n k&iacute;nh hiển vi. Bệnh nh&acirc;n ung thư c&oacute; thể được chữa trị bằng phương ph&aacute;p phẫu thuật kết hợp h&oacute;a trị liệu hoặc xạ trị. Đặc biệt những người bệnh ung thư c&oacute; thể ngăn ngừa ung thư qua chế độ ăn uống l&agrave;nh mạnh. Nếu đ&atilde; ph&aacute;t bệnh, bệnh nh&acirc;n n&ecirc;n kết hợp c&aacute;c loại thuốc t&acirc;y y v&agrave; hỗ trợ điều trị ung thư bằng thuốc nam sẽ rất hiệu quả trong đ&oacute; Nấm Linh Chi Đỏ l&agrave; loại thực phẩm chức năng d&ugrave;ng h&agrave;ng ng&agrave;y gi&uacute;p l&agrave;m chậm, l&agrave;m ti&ecirc;u giảm c&aacute;c tế b&agrave;o khối u. V&igrave; trong Nấm Linh Chi đỏ c&oacute; chứa hơn 200 loại polysaccharid được chuy&ecirc;n gia y học khuy&ecirc;n d&ugrave;ng.</p>\r\n\r\n<p><strong>Gi&aacute; trị của Nấm Linh Chi đỏ</strong></p>\r\n\r\n<p>Ngo&agrave;i c&ocirc;ng dụng tuyệt vời của Nấm Linh Chi đỏ trong việc chữa trị căn bệnh ung thư hiểm &aacute;c th&igrave; việc sử dụng Nấm Linh Chi đỏ c&ograve;n c&oacute; những c&ocirc;ng dụng v&ocirc; c&ugrave;ng tuyệt vời :</p>\r\n\r\n<ul>\r\n	<li>&nbsp;Điều tiết hệ thần kinh trun gương</li>\r\n	<li>Tăng khả năng miễn dịch</li>\r\n	<li>Cải thiện chức năng nội mạc</li>\r\n	<li>Điều h&ograve;a huyết &aacute;p, l&agrave;m bền th&agrave;nh mạch</li>\r\n	<li>Chống l&atilde;o h&oacute;a</li>\r\n	<li>Bảo vệ tế b&agrave;o, tăng cường tr&iacute; nhớ</li>\r\n	<li>Ngăn ngừa, điều trị c&aacute;c cơn hen suyễn mạn t&iacute;nh</li>\r\n	<li>Tăng cường chuyển h&oacute;a c&aacute;c chất trong cơ thể</li>\r\n	<li>Tăng khả năng tổng hợp c&aacute;c Acid nucleic</li>\r\n	<li>Cải thiện hệ tuần ho&agrave;n ngoại vi ở da, c&oacute; t&aacute;c dụng l&agrave;m đẹp.</li>\r\n</ul>\r\n', 'id2267713379_4400.jpg', '2019-01-01'),
(2, 'Thảo Dược - xu hướng chữa bệnh của người hiện đại', '<p>Việt Nam l&agrave; một đất nước c&oacute; kh&iacute; hậu Nhiệt đới ẩm gi&oacute; m&ugrave;a n&ecirc;n tạo&nbsp;điều kiện thuận lợi cho c&acirc;y cối ph&aacute;t triển trong đ&oacute; c&oacute; c&aacute;c loại c&acirc;y dược liệu. C&aacute;ch chữa bệnh bằng thảo dược&nbsp;hay c&ograve;n gọi l&agrave; chữa bệnh Đ&ocirc;ng Y mang lại nhiều lợi &iacute;ch về sức khỏe, gi&uacute;p người bệnh cải thiện sức khỏe v&agrave; điều trị bệnh rất tốt v&agrave; n&oacute; đ&atilde; tạo th&agrave;nh xu thế chữa bệnh, l&agrave; sự lựa chọn của rất nhiều người trong x&atilde; hội hiện đại ng&agrave;y nay.&nbsp;<em><strong>V&igrave; sao?</strong></em></p>\r\n\r\n<p><strong>An to&agrave;n, hiệu quả, &iacute;t g&acirc;y t&aacute;c dụng phụ</strong></p>\r\n\r\n<p>Theo như nghi&ecirc;n cứu thực tiễn, c&aacute;c loại&nbsp;thảo dược c&oacute; nguồn gốc tự nhi&ecirc;n c&oacute; rất &iacute;t hoặc kh&ocirc;ng c&oacute; t&aacute;c hại đối với người sử dụng. Chỉ cần sử dụng đ&uacute;ng c&aacute;ch sẽ tạo được hiệu quả r&otilde; rệt.&nbsp;Nhiều b&agrave;i thuốc d&acirc;n gian c&oacute; t&aacute;c dụng chữa bệnh thần k&igrave; m&agrave; nền y học hiện đại cũng&nbsp;kh&ocirc;ng thể giải th&iacute;ch được</p>\r\n\r\n<p>Nền văn h&oacute;a hội nhập kh&ocirc;ng ngừng nghỉ&nbsp;đồng nghĩa với việc con người phải đối diện với nhiều&nbsp;bất cập về c&aacute;c loại&nbsp;mặt h&agrave;ng li&ecirc;n quan đến sức khỏe&nbsp;khiến cho mọi người c&oacute; xu hướng quay lại c&aacute;c b&agrave;i thuốc c&oacute; nguồn gốc từ thảo dược thi&ecirc;n nhi&ecirc;n. Ch&iacute;nh v&igrave; vậy, xu hướng ph&aacute;t triển nền dược liệu để phục vụ cho sức khỏe cộng đồng đang được nhiều đơn vị tiến h&agrave;nh nghi&ecirc;n cứu v&agrave; sản xuất để mang lại gi&aacute; trị chữa bệnh cho con người.</p>\r\n\r\n<p><strong>B&agrave;o chế Thảo dược hiện đại, dễ d&ugrave;ng</strong></p>\r\n\r\n<p>Hiện nay, việc sử dụng&nbsp;dạng thuốc từ dược liệu đang l&agrave; xu hướng. Tận dụng nguồn dược liệu phong ph&uacute; c&ugrave;ng c&ocirc;ng nghệ sản xuất ti&ecirc;n tiến, kết hợp với c&ocirc;ng nghệ b&agrave;o chế hiện đại, Gia Bảo tự tin&nbsp;c&oacute; được hiệu quả điều trị cao m&agrave; vẫn an to&agrave;n, tiện lợi cho người ti&ecirc;u d&ugrave;ng. Đặc biệt đối với những đối tượng c&oacute; cơ địa nhạy cảm như người gi&agrave; v&agrave; trẻ nhỏ, thuốc c&oacute; nguồn gốc từ thảo dược lu&ocirc;n l&agrave; lựa chọn h&agrave;ng đầu n&ecirc;n ưu ti&ecirc;n sử dụng.</p>\r\n', 'thaoduoc.jpg', '2019-01-02'),
(3, 'Tác dụng của cây húng chanh', '<p>Nh&acirc;n d&acirc;n ta thường th&aacute;i nhỏ l&aacute; h&uacute;ng chanh để ướp thịt, c&aacute;, n&oacute; l&agrave; loại gia vị đặc sắc. L&aacute; v&agrave; ngọn non thường được d&ugrave;ng trị cảm c&uacute;m, ho hen, ho ra m&aacute;u, sốt cao, sốt kh&ocirc;ng ra mồ h&ocirc;i, n&ocirc;n ra m&aacute;u, đổ m&aacute;u cam, c&ograve;n d&ugrave;ng chữa vi&ecirc;m họng, khản tiếng.</p>\r\n\r\n<p><strong>H&uacute;ng chanh, Rau tần d&agrave;y l&aacute;, Rau thơm l&ocirc;ng &ndash; Plectranthus amboinicus (Lour.) Spreng (Coleus amboinicus Lour.), thuộc họ Hoa m&ocirc;i &ndash; Lamiaceae. M&ocirc; tả: C&acirc;y thảo c&oacute; thể sống nhiều năm, cao 20-50cm, phần th&acirc;n s&aacute;t gốc ho&aacute; gỗ. L&aacute; mọc đối d&agrave;y mọng nước, h&igrave;nh tr&aacute;i xoan rộng, d&agrave;i 3-6cm, rộng, mọc th&agrave;nh b&ocirc;ng ở ngọn th&acirc;n v&agrave; đầu c&agrave;nh, gồm những v&ograve;ng hoa d&agrave;y đặc, c&aacute;ch qu&atilde;ng nhau. Quả nhỏ, tr&ograve;n, m&agrave;u n&acirc;u, chứa 1 hạt. To&agrave;n c&acirc;y c&oacute; l&ocirc;ng rất nhỏ v&agrave; c&oacute; m&ugrave;i thơm như m&ugrave;i chanh. M&ugrave;a hoa quả th&aacute;ng 4-5.</strong></p>\r\n\r\n<p><img alt=\"7_1444664658_hung_chanh_chua_ho_cho_tre\" src=\"http://biotrade.com.vn/wp-content/uploads/2016/09/7_1444664658_hung_chanh_chua_ho_cho_tre.jpg\" /></p>\r\n\r\n<p><strong>Bộ phận d&ugrave;ng:</strong>&nbsp;L&aacute; v&agrave; ngọn non &ndash;&nbsp;<em>Folium et Gemma Plectranthi</em>.</p>\r\n\r\n<p><strong>Nơi sống v&agrave; thu h&aacute;i:</strong>&nbsp;C&acirc;y c&oacute; gốc ở quần đảo M&ocirc;lu&yacute;c (miền M&atilde; Lai) được trồng l&agrave;m gia vị v&agrave; l&agrave;m thuốc. C&oacute; thể thu h&aacute;i l&aacute; quanh năm, thường d&ugrave;ng tươi, d&ugrave;ng đến đ&acirc;u h&aacute;i đến đ&oacute;. L&uacute;c trời kh&ocirc; r&aacute;o, h&aacute;i l&aacute; b&aacute;nh tẻ, loại bỏ c&aacute;c l&aacute; s&acirc;u hay l&aacute; gi&agrave; &uacute;a v&agrave;ng, đem phơi nắng nhẹ hay sấy ở 40-45<sup>o</sup>C đến kh&ocirc;.</p>\r\n\r\n<p><strong>Th&agrave;nh phần h&oacute;a học:</strong>&nbsp;L&aacute; chứa &iacute;t tinh dầu (0,05-0,12%), trong tinh dầu c&oacute; đến 65,2% c&aacute;c hợp chất phenolic trong đ&oacute; c&oacute; salicylat, thymol, carvacrol, eugenol v&agrave; chavicol; c&ograve;n c&oacute; một chất m&agrave;u đỏ l&agrave; colein.</p>\r\n\r\n<p><strong>T&iacute;nh vị, t&aacute;c dụng:</strong>&nbsp;H&uacute;ng chanh c&oacute; vị the cay, hơi chua, m&ugrave;i thơm, t&iacute;nh ấm, kh&ocirc;ng độc, c&oacute; t&aacute;c dụng lợi phế, trừ đờm, giải cảm, l&agrave;m ra mồ h&ocirc;i, l&agrave;m th&ocirc;ng hơi, giải độc. Colein trong l&aacute; c&oacute; t&aacute;c dụng kh&aacute;ng sinh mạnh đối với một số vi tr&ugrave;ng, nhất l&agrave; ở v&ugrave;ng họng, mũi, miệng v&agrave; cả ở đường ruột.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng, chỉ định v&agrave; phối hợp:</strong>&nbsp;Nh&acirc;n d&acirc;n ta thường th&aacute;i nhỏ l&aacute; H&uacute;ng chanh để ướp thịt, c&aacute;, n&oacute; l&agrave; loại gia vị đặc sắc. L&aacute; v&agrave; ngọn non thường được d&ugrave;ng trị cảm c&uacute;m, ho hen, ho ra m&aacute;u, sốt cao, sốt kh&ocirc;ng ra mồ h&ocirc;i, n&ocirc;n ra m&aacute;u, đổ m&aacute;u cam, c&ograve;n d&ugrave;ng chữa vi&ecirc;m họng, khản tiếng. Liều d&ugrave;ng 10-15g, dạng thuốc sắc hoặc gi&atilde; lấy nước uống. C&oacute; thể d&ugrave;ng ri&ecirc;ng hoặc phối hợp với nhiều loại l&aacute; kh&aacute;c nấu nước x&ocirc;ng.</p>\r\n\r\n<p>Ở Malaixia, người ta d&ugrave;ng l&aacute; nấu cho phụ nữ sau khi sinh đẻ n&oacute;ng, l&aacute; tươi gi&atilde; ra lấy nước cốt cho trẻ em bị sổ mũi uống. D&ugrave;ng ngo&agrave;i lấy l&aacute; gi&atilde; ra đắp trị nẻ m&ocirc;i, đau bụng, đau đầu v&agrave; d&ugrave;ng xoa l&ecirc;n người khi bị sốt.</p>\r\n\r\n<p>Ở Ấn Độ, l&aacute; H&uacute;ng chanh d&ugrave;ng chữa bệnh về đường tiết niệu v&agrave; rỉ nước &acirc;m đạo. Nước &eacute;p l&aacute; trộn với đường l&agrave; một loại thuốc g&acirc;y trung tiện mạnh, cũng d&ugrave;ng trị ho v&agrave; chứng kh&oacute; ti&ecirc;u.</p>\r\n\r\n<p><strong>Đơn thuốc:</strong></p>\r\n\r\n<ol>\r\n	<li>Chữa ho, vi&ecirc;m họng, khản tiếng: L&aacute; H&uacute;ng chanh non 5-10g gi&atilde; n&aacute;t vắt lấy nước cốt n&oacute;ng. Hoặc đem gi&atilde; nhỏ một nắm l&aacute; (15-20g), th&ecirc;m nước, vắt lấy nước uống l&agrave;m hai lần trong ng&agrave;y. Đối với trẻ em, th&ecirc;m &iacute;t đường, đem hấp cơm cho uống l&agrave;m 2-3 lần.</li>\r\n	<li>Chữa đau bụng: L&aacute; H&uacute;ng chanh non rửa sạch, 1-2 l&aacute; nhai với một &iacute;t muối, ngậm nuốt dần dần.</li>\r\n	<li>Rắn cắn, b&ograve; cạp v&agrave; ong đốt: L&aacute; H&uacute;ng chanh tươi gi&atilde; đắp</li>\r\n</ol>\r\n', 'hungchanh.jpg', '2019-01-03'),
(4, 'Ngải cứu - Cây thuốc vườn nhà', '<p>Ngải cứu&nbsp;l&agrave; một c&acirc;y thuốc nam c&oacute; rất nhiều c&ocirc;ng dụng chữa c&aacute;c bệnh như đau đầu , điều h&ograve;a kinh nguyệt, an thai, trị mụn, lưu th&ocirc;ng m&aacute;u l&ecirc;n n&atilde;o&hellip;</p>\r\n\r\n<p><strong>C&acirc;y ngải cứu c&ograve;n c&oacute; t&ecirc;n trong d&acirc;n gian l&agrave; c&acirc;y thuốc cứu, c&acirc;y thuốc cao hay ngải điệp c&oacute; t&ecirc;n khoa học Artemisia vulgaris L. họ C&uacute;c Asteraceae. T&aacute;c dụng của ngải cứu v&ocirc; c&ugrave;ng tốt, đ&acirc;y l&agrave; một c&acirc;y thuốc nam c&oacute; rất nhiều c&ocirc;ng dụng chữa c&aacute;c bệnh như đau đầu , điều h&ograve;a kinh nguyệt, an thai, trị mụn, lưu th&ocirc;ng m&aacute;u l&ecirc;n n&atilde;o&hellip;</strong></p>\r\n\r\n<p><img alt=\"ngai-cuu\" src=\"http://biotrade.com.vn/wp-content/uploads/2016/09/ngai-cuu.jpg\" /></p>\r\n\r\n<p><strong>C&aacute;c bệnh chữa từ ngải cứu:</strong></p>\r\n\r\n<p>1- Đau đầu: L&aacute; ngải cứu non, tươi 150g, trứng g&agrave; 2 quả.</p>\r\n\r\n<p>C&aacute;ch l&agrave;m: L&aacute; ngải cứu rửa sạch để r&aacute;o th&aacute;i nhỏ. Cho trứng v&agrave;o đ&aacute;nh tan c&ugrave;ng với l&aacute; ngải cứu đ&atilde; th&aacute;i nhỏ, th&ecirc;m gia vị đem r&aacute;n với dầu ăn. Ng&agrave;y l&agrave;m 1 lần v&agrave;o buổi s&aacute;ng hoặc tối. Ăn khi c&ograve;n n&oacute;ng. D&ugrave;ng li&ecirc;n tục trong 7-10 ng&agrave;y. Hoặc mỗi th&aacute;ng n&ecirc;n ăn trong 10 ng&agrave;y c&oacute; c&ocirc;ng hiệu gi&uacute;p lưu th&ocirc;ng m&aacute;u l&ecirc;n n&atilde;o. B&agrave;i thuốc n&agrave;y dễ l&agrave;m v&agrave; c&oacute; hiệu quả tốt để trị chứng đau đầu.</p>\r\n\r\n<p>2-Mụn ở mặt: D&ugrave;ng l&aacute; non l&agrave;m mặt nạ 20 ph&uacute;t sau đ&oacute; rửa sạch. D&ugrave;ng to&agrave;n th&acirc;n th&igrave; sắc lấy nước cho v&agrave;o bồn tắm, chữa r&ocirc;m sảy, mẩn ngứa, mỏi mệt.</p>\r\n\r\n<p>3-Mụn trứng c&aacute;: L&aacute; ngải cứu tươi gi&atilde; n&aacute;t, đắp l&ecirc;n mặt, để khoảng 20 ph&uacute;t, rồi rửa lại mặt, l&agrave;m li&ecirc;n tục sẽ cho bạn l&agrave;n da mịn m&agrave;ng v&agrave; trắng hồng.</p>\r\n\r\n<p>4-Đau họng: gi&atilde; ngải cứu lấy nước cốt uống từ từ, &iacute;t một, b&atilde; với &iacute;t dấm đắp b&ecirc;n ngo&agrave;i cổ, ph&iacute;a trước.</p>\r\n\r\n<p>5-Ng&atilde; tức ngực, ngất xỉu: Lấy ngay ngải cứu tươi gi&atilde; nhuyễn lấy nước ho&agrave; c&ugrave;ng một l&iacute;t rượu để uống, b&atilde; xoa đắp ngo&agrave;i.</p>\r\n\r\n<p>6-Đau bụng do lạnh: l&aacute; ngải cứu tươi, hơ n&oacute;ng chườm bụng. Ng&agrave;y l&agrave;m 2-3 lần.</p>\r\n\r\n<p>7-Đau lưng lấy l&aacute; ngải cứu x&agrave;o dấm đặt l&ecirc;n giường, l&oacute;t l&aacute; chuối hoặc nilon nằm ngửa đặt lưng l&ecirc;n ngải cứu. Hoặc nằm sấp, đắp chườm ngải cứu l&ecirc;n v&ugrave;ng thắt lưng.</p>\r\n\r\n<p>8-Đau lưng do gai cột sống: Ngải cứu tươi 250g, dấm gạo 150ml, miếng vải mỏng, mềm. Ngải cứu rửa sạch, gi&atilde; n&aacute;t. Dấm đun cho n&oacute;ng. D&ugrave;ng mảnh vải g&oacute;i ngải cứu gi&atilde; n&aacute;t trộn với dấm đ&atilde; đun n&oacute;ng đem xoa dọc theo xương sống chừng 15 ph&uacute;t, trong qu&aacute; tr&igrave;nh xoa, n&ecirc;n h&acirc;m n&oacute;ng thuốc thường xuy&ecirc;n. N&ecirc;n thực hiện v&agrave;o buổi tối trước khi đi ngủ. Mỗi liệu tr&igrave;nh điều trị trong 15 ng&agrave;y. V&agrave; thực hiện li&ecirc;n tục từ 3 &ndash; 5 th&aacute;ng.</p>\r\n\r\n<p>9-Đau ch&acirc;n: 1 nắm muối, 1 nắm l&aacute; ngải, nấu l&ecirc;n, đổ v&agrave;o th&ugrave;ng, ng&acirc;m ngập tới gần đầu gối. V&agrave;i lần sẽ bớt đau, đi lại dễ d&agrave;ng.</p>\r\n\r\n<p>10-Điều kinh: Một tuần lễ trước dự kỳ c&oacute; kinh, uống mỗi ng&agrave;y từ 6 đến 12g (tối đa 20g), sắc với nước hay h&atilde;m với nước s&ocirc;i như h&atilde;m ch&egrave;, chia l&agrave;m 3 lần uống trong ng&agrave;y. C&oacute; thể uống dưới dạng thuốc bột (5-10g) hay dưới dạng thuốc cao đặc 1-4g. Nếu c&oacute; thai, thuốc kh&ocirc;ng g&acirc;y sẩy thai v&igrave; kh&ocirc;ng c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch đối với tử cung c&oacute; thai.</p>\r\n\r\n<p>11-Kinh nguyệt k&eacute;o d&agrave;i, m&aacute;u ra nhiều, người mệt mỏi, đi đứng mệt yếu: h&agrave;ng th&aacute;ng đến ng&agrave;y bắt đầu h&agrave;nh kinh v&agrave; cả những ng&agrave;y đang c&oacute; kinh, uống s&aacute;ng một lần, chiều một lần theo đơn thuốc sau đ&acirc;y: L&aacute; ngải cứu kh&ocirc; 10g, th&ecirc;m 200ml nước, c&ograve;n 100ml, th&ecirc;m &iacute;t đường cho dễ uống.</p>\r\n\r\n<p>C&oacute; thể c&acirc;n lu&ocirc;n một lần 20g sắc với 400ml nước, c&ograve;n 200ml, chia l&agrave;m 2 lần uống s&aacute;ng v&agrave; chiều. Chỉ sau 1-2 ng&agrave;y thấy kết quả.</p>\r\n\r\n<p>12-An thai: Những người đang mang thai, nếu thấy c&oacute; hiện tượng đau bụng, ra m&aacute;u, 50g l&aacute; ngải cứu tươi, rửa sạch, gi&atilde; n&aacute;t chắt lấy nước uống, n&ecirc;n cho &iacute;t đường dễ uống. Ng&agrave;y uống 1 lần sau khi ăn s&aacute;ng.</p>\r\n\r\n<p>13-Những người kiệt sức hay c&aacute;c b&agrave; mẹ đang cho con b&uacute;: Lấy 5 c&agrave;nh l&aacute; ngải cứu tươi (hoặc kh&ocirc;), rửa sạch, băm nhỏ, pha với một ly nước s&ocirc;i, uống h&agrave;ng ng&agrave;y sẽ mau hồi phục sức khỏe.</p>\r\n\r\n<p>14-Đi lỵ ra m&aacute;u, th&acirc;n nhiệt n&oacute;ng, vi&ecirc;m xoang mũi, chảy m&aacute;u cam, phụ nữ bị sản hậu, băng huyết: D&ugrave;ng 200gr l&aacute; thuốc cứu sao v&agrave;ng, nấu trong 250ml nước c&ograve;n 100ml. Chia l&agrave;m 2 phần, uống trong ng&agrave;y. C&oacute; thể t&aacute;n nhuyễn, h&atilde;m 50ml nước s&ocirc;i với 10gr thuốc cứu bột, uống mỗi l&uacute;c kh&aacute;t, li&ecirc;n tục 2 ng&agrave;y.</p>\r\n\r\n<p>15-Đau thần kinh tọa, nhức buốt khớp xương ch&acirc;n tay, c&aacute;c đốt xương cột sống, đau đầu hoa mắt, ghẻ lở do nghiện rượu, ngộ độc rượu lạ: D&ugrave;ng 300gr thuốc cứu rửa sạch, gi&atilde; n&aacute;t, th&ecirc;m 2 muỗng mật ong , vắt lấy nước uống trưa, chiều. Li&ecirc;n tục 1-2 tuần.</p>\r\n\r\n<p>16-Trẻ em bị sốt cao cũng l&agrave;m như tr&ecirc;n nhưng chỉ để xoa khắp m&igrave;nh, trừ đầu mặt kh&ocirc;ng xoa, kh&ocirc;ng uống.</p>\r\n\r\n<p>17-Trị ngứa, ghẻ lở, r&ocirc;m sảy ở trẻ con: Với những trẻ nhỏ thường hay bị r&ocirc;m sảy, xay n&aacute;t l&aacute; ngải cứu, lọc lấy nước cốt rồi h&ograve;a v&agrave;o nước tắm của trẻ. L&agrave;m li&ecirc;n tục trong v&agrave;i ng&agrave;y, c&aacute;c nốt ngứa sẽ lặn mất.</p>\r\n\r\n<p>&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;</p>\r\n\r\n<p><strong>*Cấm kỵ</strong></p>\r\n\r\n<p><em>Kh&ocirc;ng n&ecirc;n d&ugrave;ng d&agrave;i ng&agrave;y.</em></p>\r\n\r\n<p><em>Người nhiệt &acirc;m hư kh&ocirc;ng d&ugrave;ng, người cao huyết &aacute;p do &acirc;m hư hoả vượng, kh&ocirc;ng c&oacute; h&agrave;n thấp, thai sản b&igrave;nh thường kh&ocirc;ng d&ugrave;ng.</em></p>\r\n', 'ngaicuu.jpg', '2019-01-03'),
(5, 'Công nghệ Nano Curcumin', '<p><strong>Nano Curcumin (Tinh nghệ&nbsp;Nano) l&agrave; dạng b&agrave;o chế c&ocirc;ng nghệ cao của Curcumin chiết xuất từ củ nghệ v&agrave;ng. Nano Curcumin c&oacute; hiệu quả vượt trội so với curcumin, gi&uacute;p cải thiện c&aacute;c vấn đề kh&oacute; khăn l&agrave;m hạn chế hiệu quả hỗ trợ điều trị của Curcumin</strong>.&nbsp;</p>\r\n\r\n<h2><strong>Nano Curcumin l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>Ng&agrave;y nay, c&aacute;c nh&agrave; khoa học đ&atilde; kh&aacute;m ph&aacute; ra, hoạt chất tạo n&ecirc;n m&agrave;u v&agrave;ng v&agrave; đem lại t&aacute;c dụng của nghệ v&agrave;ng&nbsp;l&agrave; tinh chất Curcumin, chỉ chiếm 0,3% khối lượng. C&ocirc;ng dụng của Curcumin, đang thu h&uacute;t được sự quan t&acirc;m của ng&agrave;nh Y học tr&ecirc;n to&agrave;n thế giới. Thư viện Pubmed c&oacute; tới 5612 b&agrave;i b&aacute;o nghi&ecirc;n cứu về t&aacute;c dụng của hoạt chất Curcumin. Từ năm 2008, đ&atilde; c&oacute; rất nhiều thử nghiệm l&acirc;m s&agrave;ng tr&ecirc;n người để nghi&ecirc;n cứu về t&aacute;c dụng của&nbsp;<strong>Curcumin</strong>.</p>\r\n\r\n<p>Curcumin đ&atilde; được c&aacute;c nh&agrave; khoa học khẳng định hiệu quả trong việc hỗ trợ điều trị c&aacute;c bệnh mạn t&iacute;nh, nan y như:&nbsp;<strong>ung thư,&nbsp;</strong>vi&ecirc;m lo&eacute;t dạ d&agrave;y t&aacute; tr&agrave;ng, tuy nhi&ecirc;n r&agrave;o cản lớn khiến tinh chất nghệ Curcumin chưa được ứng dụng rộng r&atilde;i l&agrave; do Curcumin kh&ocirc;ng tan trong nước (độ tan 0,001%), sinh khả dụng thấp.</p>\r\n\r\n<p>V&igrave; vậy, khi d&ugrave;ng theo đường uống, Curcumin h&ograve;a tan một phần rất nhỏ v&agrave;o c&aacute;c dịch thể của ống ti&ecirc;u h&oacute;a, chỉ 7 &ndash; 10% Curcumin được hấp thu v&agrave;o m&aacute;u, lại bị chuyển h&oacute;a nhanh qua gan, l&agrave;m cho sinh khả dụng thực tế của Curcumin chỉ đạt 2-3%.&nbsp;</p>\r\n\r\n<p>Do đ&oacute; để đạt được liều 12g/ng&agrave;y như c&aacute;c nh&agrave; khoa&nbsp; học&nbsp; khuy&ecirc;n&nbsp; d&ugrave;ng&nbsp; th&igrave;&nbsp; mỗi&nbsp; ng&agrave;y&nbsp; phải&nbsp; uống&nbsp; tới&nbsp; 24&nbsp; vi&ecirc;n&nbsp; nang Curcumin 500mg. Đ&oacute; l&agrave; liều qu&aacute; cao, bệnh nh&acirc;n kh&ocirc;ng thể chịu đựng nổi m&ugrave;i vị kh&oacute; chịu, g&acirc;y cảm gi&aacute;c buồn n&ocirc;n, kh&oacute; tu&acirc;n thủ v&agrave; chi ph&iacute; qu&aacute; cao. C&ograve;n nếu chỉ uống với liều th&ocirc;ng thường như hiện tại th&igrave; chưa đủ h&agrave;m lượng&nbsp;<strong>Curcumin</strong>&nbsp;trong m&aacute;u để ph&aacute;t huy hiệu quả như mong muốn</p>\r\n\r\n<p>Để tăng tối đa hấp thu tinh chất Curcumin đầy tiềm năng, rất nhiều phương ph&aacute;p đ&atilde; được &aacute;p dụng như nano h&oacute;a, bổ sung c&aacute;c chất phụ trợ bioperin, tổng hợp đồng ph&acirc;n, tạo phức chelat của Curcumin với c&aacute;c kim loại... Trong đ&oacute; c&ocirc;ng nghệ nano mang lại hiệu quả vượt trội hơn cả trong việc n&acirc;ng cao độ tan v&agrave; hấp thu của Curcumin, đồng thời tạo ra một số lợi &iacute;ch như cải thiện sự hấp thu của tế b&agrave;o, giảm chuyển h&oacute;a tại gan, giữ ổn định nồng độ trong m&aacute;u, tăng cường dược t&iacute;nh của curcumin.</p>\r\n\r\n<p>T&agrave;i liệu nghi&ecirc;n cứu đầu ti&ecirc;n về&nbsp;Nano Curcumin&nbsp;ứng dụng trong y học được xuất bản v&agrave;o năm 2005, từ đ&oacute; đến nay Nano Curcumin đ&atilde; trở th&agrave;nh t&acirc;m điểm nghi&ecirc;n cứu của c&aacute;c nh&agrave; khoa học tr&ecirc;n thế giới với 254 bằng ph&aacute;t minh s&aacute;ng chế của c&aacute;c quốc gia c&oacute; nền y tế kĩ thuật ph&aacute;t triển như Mỹ, Anh, Đức, Nhật, H&agrave;n Quốc... Trong đ&oacute; bằng ph&aacute;t minh c&oacute; li&ecirc;n quan đến ứng dụng Nano Curcumin cho bệnh nh&acirc;n ung thư chiếm tỉ lệ lớn nhất (24%), bệnh tim mạch đứng ở vị tr&iacute; thứ hai với tỉ lệ l&agrave; 13%. &nbsp;&nbsp;</p>\r\n\r\n<p>Bằng c&ocirc;ng nghệ Nano, c&aacute;c ph&acirc;n tử Curcumin được cố định trong chất liệu polymer, tạo th&agrave;nh c&aacute;c hạt tiểu ph&acirc;n Nano c&oacute; k&iacute;ch thước si&ecirc;u nhỏ cỡ 30nm - 100nm. Bạn h&atilde;y tưởng tượng nếu ph&oacute;ng to 1 vật thể c&oacute; k&iacute;ch thước nm l&ecirc;n bằng 1 quả cam, th&igrave; quả cam sẽ to bằng tr&aacute;i đất</p>\r\n\r\n<p><img alt=\"Công nghệ nano giúp tăng hiệu quả của curcumin\" src=\"https://cumargold.vn/FileUpload/Images/360x276xcong_nghe_nano.jpg.pagespeed.ic.-mgQFF0A41.webp\" /></p>\r\n\r\n<p><strong><strong><strong><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</em></strong></strong></strong></strong><em>&nbsp;</em></p>\r\n\r\n<p><em>C&ocirc;ng nghệ nano ph&oacute;ng đại mọi thứ l&ecirc;n gấp nhiều lần&nbsp;(Nguồn: sưu tầm)</em></p>\r\n\r\n<h2><br />\r\n<strong>Ưu điểm vượt trội của Nano Curcumin</strong></h2>\r\n\r\n<p><strong>&nbsp;</strong>- Với k&iacute;ch thước si&ecirc;u nhỏ từ 50nm-70nm gi&uacute;p&nbsp;<strong><em>Nano Curcumin</em></strong>&nbsp;ph&acirc;n t&aacute;n tốt trong nước, độ tan 7,5%, tăng gấp 7.500 lần so với curcumin (0,001%)<br />\r\n<br />\r\n- K&iacute;ch thước nano si&ecirc;u nhỏ gi&uacute;p&nbsp;<strong>Nano Curcumin thẩm thấu v&agrave;o m&aacute;u nhanh ch&oacute;ng để ph&aacute;t huy hiệu quả hỗ trợ điều trị</strong>&nbsp;tại hầu hết c&aacute;c cơ quan kh&aacute;c nhau trong cơ thể.<br />\r\n<br />\r\n- Độ hấp thu&nbsp;của Nano Curcumin đạt tới 95%, hiệu quả vượt trội so với Curcumin th&ocirc;ng thường (Theo kết quả nghi&ecirc;n cứu của nh&oacute;m chuy&ecirc;n gia của trường Đại học Dược, bang Ohio, Hoa kỳ, thực hiện năm 2012 đăng tr&ecirc;n Pubmed.&nbsp;<br />\r\n- Với độ hấp thu qua đường uống l&ecirc;n tới 95%, theo t&iacute;nh to&aacute;n của c&aacute;c chuy&ecirc;n gia th&igrave; liều sử dụng h&agrave;ng ng&agrave;y để đạt hiệu quả hỗ trợ điều trị l&agrave; 2 -4 vi&ecirc;n nang Nano Curcumin thay v&igrave; phải ăn 4kg nghệ tươi, uống 120g (hơn 1 lạng)&nbsp;<strong>tinh bột nghệ</strong>&nbsp;hay uống tới 24 vi&ecirc;n nang Curcumin.</p>\r\n', 'nano.png', '2019-01-04'),
(6, 'Tác dụng thần kì của bột nghệ vàng', '<p>Uống bột nghệ c&oacute; t&aacute;c dụng g&igrave;? Với v&ocirc; v&agrave;n c&ocirc;ng dụng kh&aacute;c nhau cho sức khỏe v&agrave; l&agrave;n da, tuy nhi&ecirc;n chỉ cần nghe uống bột nghệ h&agrave;ng ng&agrave;y đ&uacute;ng c&aacute;ch da mịn m&agrave;ng như em b&eacute; l&agrave; chị em đ&atilde; y&ecirc;u qu&yacute; ngay nguy&ecirc;n liệu n&agrave;y rồi.</p>\r\n\r\n<h2><strong>Bột nghệ v&agrave;ng c&oacute; t&aacute;c dụng g&igrave;?</strong></h2>\r\n\r\n<p>Bột nghệ v&agrave;ng c&oacute; t&aacute;c dụng g&igrave;</p>\r\n\r\n<p>Sở hữu nhiều c&ocirc;ng dụng đối với sức khỏe v&agrave; l&agrave;m đẹp, bột nghệ l&agrave; nguy&ecirc;n liệu được nhiều chị em &aacute;p dụng trong c&ocirc;ng thức l&agrave;m đẹp da hiện nay. C&ugrave;ng ADIVA điểm danh 12 c&ocirc;ng dụng nổi bật của &ldquo;thần dược&rdquo; n&agrave;y nh&eacute;!</p>\r\n\r\n<ol>\r\n	<li>Điều trị đau bao tử, đau dạ d&agrave;y, vi&ecirc;m lo&eacute;t dạ d&agrave;y, h&agrave;nh t&aacute; tr&agrave;ng, đau bụng k&eacute;m ăn, ăn kh&ocirc;ng ti&ecirc;u..</li>\r\n	<li>Hỗ trợ chức năng gan, giải độc gan, ph&ograve;ng ngừa xơ gan, vi&ecirc;m gan c&aacute;c loại&hellip;</li>\r\n	<li>Bột nghệ gi&uacute;p cơ thể chống lại c&aacute;c vi khuẩn sống k&yacute; sinh ở trong ruột, đặc biệt rất tốt cho hệ ti&ecirc;u ho&aacute; v&agrave; chống h&ocirc;i miệng.</li>\r\n	<li>D&ugrave;ng cho phụ nữ sau sinh gi&uacute;p nhanh ch&oacute;ng hồi phục sức khỏe, nhan sắc, da dẻ hồng h&agrave;o mịn m&agrave;ng. Đặc biệt l&agrave; tr&aacute;nh hậu sản&hellip;</li>\r\n	<li>Ph&ograve;ng ngừa v&agrave; hỗ trợ điều trị ung thư hiệu quả, đặc biệt ko g&acirc;y t&aacute;c dụng phụ khi sử dụng.</li>\r\n	<li>Gi&uacute;p giảm c&acirc;n, lưu th&ocirc;ng v&agrave; lọc m&aacute;u.</li>\r\n	<li>Bột nghệ cải thiện sức khỏe da đầu.</li>\r\n	<li>Bột nghệ v&agrave;ng c&ograve;n c&oacute; t&aacute;c dụng trị mụn trứng c&aacute;, n&aacute;m, th&acirc;m, sẹo, tẩy da chết,&hellip;</li>\r\n	<li>D&ugrave;ng bột nghệ cải thiện sắc tố da b&ecirc;n ngo&agrave;i bằng việc b&ocirc;i đắp v&agrave; uống để chăm da từ b&ecirc;n trong.</li>\r\n	<li>Bột nghệ cải thiện da bị kh&ocirc; v&agrave; dầu.</li>\r\n	<li>Bột nghệ x&oacute;a mờ vết nhăn quanh mắt.</li>\r\n	<li>Bột nghệ cải thiện t&igrave;nh trạng bị nứt g&oacute;t ch&acirc;n.</li>\r\n</ol>\r\n\r\n<p>&hellip;</p>\r\n\r\n<h2><strong>Uống bột nghệ c&oacute; t&aacute;c dụng g&igrave;?</strong></h2>\r\n\r\n<p><img alt=\"uong-bot-nghe-co-tac-dung-gi\" src=\"https://adiva.com.vn/wp-content/uploads/2017/05/uong-bot-nghe-co-tac-dung-gi1.jpg\" title=\"uong-bot-nghe-co-tac-dung-gi\" /></p>\r\n\r\n<p>Bột nghệ v&agrave;ng c&oacute; t&aacute;c dụng g&igrave;</p>\r\n\r\n<p>Việc uống bột nghệ h&agrave;ng ng&agrave;y c&oacute; t&aacute;c dụng g&igrave; l&agrave; c&acirc;u hỏi rất nhiều người quan t&acirc;m.</p>\r\n\r\n<h3><em>Uống bột nghệ h&agrave;ng ng&agrave;y c&oacute; t&aacute;c dụng g&igrave;?</em></h3>\r\n\r\n<p>Mỗi ng&agrave;y bạn chỉ cần uống 2 ly bột nghệ v&agrave;ng trước hoặc sau khi ăn s&aacute;ng v&agrave; tối 30 ph&uacute;t, đồng thời kết hợp với việc chăm s&oacute;c da bằng c&aacute;ch đắp mặt nạ tuần 3 lần sẽ gi&uacute;p cơ thể khỏe mạnh. Sở hữu l&agrave;n da trắng s&aacute;ng, sạch mụn v&agrave; vết th&acirc;m, vết n&aacute;m, t&agrave;n nhang một c&aacute;ch hiệu quả.</p>\r\n\r\n<p><img alt=\"Bot nghe vang co tac dung gi\" src=\"https://adiva.com.vn/wp-content/uploads/2017/05/uong-bot-nghe-co-tac-dung-gi2.jpg\" title=\"Bot nghe vang co tac dung gi\" /></p>\r\n\r\n<p>Uống bột nghệ c&oacute; t&aacute;c dụng g&igrave;</p>\r\n\r\n<h3><em><strong>C&aacute;ch d&ugrave;ng để uống như sau</strong>:</em></h3>\r\n\r\n<ul>\r\n	<li>Lấy 02 đến 03 th&igrave;a c&agrave; ph&ecirc; tinh bột nghệ, h&ograve;a tan v&agrave;o một cốc khoảng 100ml nước lọc (d&ugrave;ng nước ấm khoảng 40 độ) hoặc c&oacute; thể kết hợp th&ecirc;m với 1 trong những loại nước như cam, chanh, đường, sữa hoặc mật ong rồi uống sẽ ngon hơn.</li>\r\n	<li>Mỗi ng&agrave;y cần uống 02 lần trước hoặc sau bữa ăn để c&oacute; kết quả như &yacute; muốn.</li>\r\n</ul>\r\n\r\n<p>Bạn c&oacute; biết v&igrave; sao n&ecirc;n pha bột nghệ với nước ấm hay kh&ocirc;ng?&nbsp; Bởi v&igrave; &nbsp;bột nghệ v&agrave;o sẽ nhanh tan v&agrave; hơn thế nữa sẽ giữ được t&iacute;nh chất qu&yacute; của nghệ . Mặt kh&aacute;c nước ấm sẽ l&agrave;m chất curcumin dễ ngấm v&agrave; ngấm nhanh hơn v&agrave;o th&agrave;nh ruột v&agrave; dạ d&agrave;y của bạn, điều n&agrave;y rất tốt cho ti&ecirc;u h&oacute;a cũng như hỗ trợ điều trị đau , vi&ecirc;m dạ d&agrave;y.</p>\r\n\r\n<p>Thế l&agrave; bạn đ&atilde; biết việc uống bột nghệ c&oacute; t&aacute;c dụng g&igrave; rồi chứ! Đừng bỏ qua một nguy&ecirc;n liệu dễ t&igrave;m n&agrave;y nh&eacute;! N&oacute; vừa hỗ trợ điều trị , ngăn ngừa c&aacute;c bệnh v&agrave; c&ograve;n l&agrave;m đẹp da, gi&uacute;p bạn lu&ocirc;n sở hữu l&agrave;n da hồng h&agrave;o khỏe mạnh.</p>\r\n\r\n<p>Th&ocirc;ng qua những th&ocirc;ng tin hữu &iacute;ch tr&ecirc;n c&aacute;c bạn đ&atilde; nắm được c&aacute;c t&iacute;nh chất thần kỳ của bột nghệ v&agrave;ng v&agrave; uống bột nghệ c&oacute; t&aacute;c dụng g&igrave;? Với những lợi &iacute;ch v&agrave; c&ocirc;ng dụng hữu hiệu m&agrave; bột nghệ v&agrave;ng mang lại th&igrave; chẳng c&oacute; l&yacute; do g&igrave; m&agrave; ch&uacute;ng ta kh&ocirc;ng sử dụng n&oacute; để bảo vệ sức khỏe v&agrave; chăm s&oacute;c l&agrave;n da cho bản th&acirc;n v&agrave; những người trong gia đ&igrave;nh.</p>\r\n', 'nghevang.png', '2019-01-05'),
(7, 'Phòng chống ung thư bằng nấm Linh chi', '<p>C&aacute;c nghi&ecirc;n cứu về chức năng chống khối u của nấm linh chi bắt đầu v&agrave;o những năm 1970. Nấm linh chi được nghi&ecirc;n cứu chuy&ecirc;n s&acirc;u về t&aacute;c dụng dược l&yacute; của loại nấm linh đối với việc ngăn ngừa v&agrave; ph&ograve;ng chống ung thư.</p>\r\n\r\n<p>Kết quả của nhiều nghi&ecirc;n cứu đ&atilde; cho thấy nấm linh chi đ&oacute;ng g&oacute;p một phần rất quan trọng trong việc điều trị bệnh ung thư.&nbsp;Những lợi &iacute;ch sức khỏe ch&iacute;nh của loại thảo dược n&agrave;y được thể hiện trong việc &rdquo; ức chế sự ph&aacute;t triển khối u &rdquo; v&agrave; &ldquo;kiềm chế sự l&acirc;y lan của c&aacute;c tế b&agrave;o ung thư&rdquo;.</p>\r\n\r\n<p>Khi sử dụng kết hợp với thuốc t&acirc;y, nấm linh chi kh&ocirc;ng chỉ gi&uacute;p bạn đạt được kết quả điều trị tốt hơn. N&oacute; cũng l&agrave;m giảm đ&aacute;ng kể những t&aacute;c dụng phụ do thuốc t&acirc;y g&acirc;y n&ecirc;n .</p>\r\n\r\n<p>Tất nhi&ecirc;n, ứng dụng nấm linh chi trong việc điều trị ung thư kh&ocirc;ng đảm bảo loại bỏ ho&agrave;n to&agrave;n c&aacute;c tế b&agrave;o ung thư t&ugrave;y theo mỗi trường hợp v&agrave; cơ địa kh&aacute;c nhau như thế n&agrave;o .</p>\r\n\r\n<p>Tuy nhi&ecirc;n, nhiều kết quả nghi&ecirc;n cứu chỉ ra rằng dược t&iacute;nh của nấm linh chi c&oacute; t&aacute;c động hiệu quả tốt nhất trong việc ngăn ngừa ph&aacute;t bệnh ung thư hơn chống lại sự ph&aacute;t triển ung thư. &nbsp;V&agrave; liều lượng d&ugrave;ng nấm &nbsp;linh chi trong từng trường hợp thấp hoặc cao t&ugrave;y theo từng giai đoạn của bệnh ung thư.</p>\r\n\r\n<p>Ph&ograve;ng chống ung thư bằng nấm linh chi được th&iacute; nghiệm tr&ecirc;n loại Chuột.Một nghi&ecirc;n cứu được tiến h&agrave;nh bởi gi&aacute;o sư Ching Hua-Su của Trường Cao đẳng Y Tế &nbsp;tại Đ&agrave;i Bắc đ&atilde; ph&aacute;t hiện một số t&aacute;c dụng đ&aacute;ng kh&iacute;ch lệ về chức năng chống ung thư của nấm linh chi.</p>\r\n\r\n<p><a href=\"https://www.linhchitienthao.com/wp-content/uploads/2015/12/PhcogMag_2013_9_34_167_111286_f6.jpg\"><img alt=\"phong-chong-ung-thu-bang-nam-linh-chi\" src=\"https://www.linhchitienthao.com/wp-content/uploads/2015/12/PhcogMag_2013_9_34_167_111286_f6.jpg\" /></a></p>\r\n\r\n<p>Th&iacute; Nghiệm Về Chống Ung Thư Của Linh Chi Tr&ecirc;n Chuột Bạch</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong th&iacute; nghiệm của Gi&aacute;o Sư Ching Hua- Su, một số con chuột được ti&ecirc;m 1mg dược chất Polysaccharides trong nấm linh chi tr&ecirc;n mỗi kg trọng lượng cơ thể của con chuột đều đặn mỗi ng&agrave;y trong một tuần.&nbsp;C&aacute;c kết quả thử nghiệm cho thấy số lượng c&aacute;c tế b&agrave;o ung thư c&ograve;n lại trong cơ thể của c&aacute;c nh&oacute;m điều trị với những con chuột n&agrave;y l&agrave; chỉ c&oacute; 4% so với nh&oacute;m kiểm so&aacute;t, những con chuột m&agrave; kh&ocirc;ng cần điều trị ph&ograve;ng ngừa.</p>\r\n\r\n<p>Điều n&agrave;y r&otilde; r&agrave;ng cho thấy rằng nấm linh chi kh&ocirc;ng chỉ hiệu quả trong việc ngăn ngừa sự khởi ph&aacute;t của bệnh ung thư nhưng cũng hỗ trợ trong việc ngăn chặn sự ph&aacute;t triển của c&aacute;c tế b&agrave;o ung thư l&acirc;y lan nan. N&oacute; cũng g&oacute;p phần l&agrave;m giảm thiểu những rủi ro t&aacute;i ph&aacute;t sau đ&oacute;.</p>\r\n\r\n<h3><strong>Ph&ograve;ng Chống Ung Thư Bằng Nấm Linh Chi .Trong Th&iacute; Nghiệm Vitro</strong><br />\r\nThật th&uacute; vị, trong ống nghiệm (b&ecirc;n ngo&agrave;i cơ thể) th&iacute; nghiệm thực hiện bởi gi&aacute;o sư Lin Zhi Bin của Trường Cao đẳng Y Tế tại Bắc Kinh cũng cho kết quả tương tự.</h3>\r\n\r\n<p>Trong th&iacute; nghiệm của m&igrave;nh, Gi&aacute;o sư Lin nu&ocirc;i thực b&agrave;o (c&oacute; khả năng nuốt những kẻ x&acirc;m nhập v&agrave; c&aacute;c tổ chức kh&aacute;c nhau c&oacute; hại t&igrave;m thấy trong cơ thể) hoặc c&aacute;c tế b&agrave;o l&aacute; l&aacute;ch (c&aacute;c trung t&acirc;m sản xuất c&aacute;c tế b&agrave;o miễn dịch) c&ugrave;ng với Chất Polysaccharides của nấm linh chi trong 24 giờ.</p>\r\n\r\n<p>Khi &ocirc;ng đ&atilde; pha trộn c&aacute;c nền văn h&oacute;a với c&aacute;c bạch cầu người (HL-60), &ocirc;ng ph&aacute;t hiện ra rằng văn h&oacute;a c&oacute; thể khuất phục đ&aacute;ng kể sự gia tăng của bạch cầu v&agrave; dần dần ti&ecirc;u diệt c&aacute;c tế b&agrave;o ung thư.</p>\r\n\r\n<p><strong>Ph&ograve;ng Chống Ung Thư Bằng Nấm Linh Chi . Chức Năng Củng Cố Hệ Thống Miễn Dịch.</strong></p>\r\n\r\n<p>Từ tế b&agrave;o, động vật hoặc trong c&aacute;c th&iacute; nghiệm Vitro, kết quả nghi&ecirc;n cứu đ&atilde; cho thấy chung một kết luận. Th&ocirc;ng qua chủ yếu l&agrave; &ldquo;củng cố hệ miễn dịch&rdquo; v&agrave; kh&ocirc;ng g&acirc;y hại tế b&agrave;o b&igrave;nh thường, nấm linh chi c&oacute; thể củng cố khả năng ph&ograve;ng vệ miễn dịch của trong việc x&aacute;c định v&agrave; ti&ecirc;u diệt c&aacute;c tế b&agrave;o bất thường.</p>\r\n\r\n<p>Điều n&agrave;y bao gồm l&agrave;m sống lại thực b&agrave;o, bạch cầu trung t&iacute;nh, s&aacute;t thủ tự nhi&ecirc;n hoặc c&aacute;c tế b&agrave;o NK, tế b&agrave;o T, kh&aacute;ng thể, vv v&agrave; khuyến kh&iacute;ch sự ph&aacute;t triển của đại thực b&agrave;o.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, nấm linh chi cũng kh&ocirc;i phục lại c&aacute;c tế b&agrave;o dendron cũng như tăng cường sự tiết hormone của tế b&agrave;o miễn dịch, chẳng hạn như khối u hoại tử Factor-alpha, Interleukin-1, Interleukin-2, Interleukin-6, v&agrave; Interferon Gamma v&agrave; kh&ocirc;i phục lại c&aacute;c họ tương ứng.</p>\r\n\r\n<p>Ở kh&iacute;a cạnh n&agrave;y, c&aacute;c th&agrave;nh phần hoạt động ch&iacute;nh l&agrave; nhờ chất Polysaccharides c&oacute; trong nấm linh chi v&agrave; nhiều nhất trong nấm linh chi đỏ.</p>\r\n\r\n<p><img alt=\"phong-chong-ung-thu-bang-nam-linh-chi\" src=\"https://www.linhchitienthao.com/wp-content/uploads/2015/12/ganoderma-10.jpg\" /></p>\r\n\r\n<p>Nấm Linh Chi Đỏ C&oacute; Chứa Nhiều Chất Polysaccharides</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4><strong>&nbsp;Ph&ograve;ng Chống Ung Thư Bằng Nấm Linh Chi V&agrave; C&aacute;ch Điều Trị Bệnh Ung Thư Của Nấm Linh Chi</strong></h4>\r\n\r\n<p>Như c&aacute;c kết quả nghi&ecirc;n cứu cho thấy nấm linh chi hiệu quả trong việc ph&ograve;ng ngừa v&agrave; điều trị c&aacute;c khối u xuất ph&aacute;t từ khả năng của m&igrave;nh để củng cố hệ thống miễn dịch, n&oacute; c&oacute; lợi trong bảo vệ chống lại thực tế tất cả c&aacute;c loại bệnh ung thư.</p>\r\n\r\n<p>Nghi&ecirc;n cứu l&acirc;m s&agrave;ng đ&atilde; chỉ ra rằng việc sử dụng nấm linh chi đối với bệnh nh&acirc;n đang qu&aacute; tr&igrave;nh phẫu thuật cắt bỏ, điều trị radio, v&agrave; liệu ph&aacute;p h&oacute;a trị rất c&oacute; hiệu quả trong việc cải thiện c&aacute;c điều kiện của bệnh ung thư dạ d&agrave;y, ung thư phổi, ung thư gan, ung thư đại tr&agrave;ng, ung thư tuyến tụy, ung thư thận, ung thư tuyến tiền liệt, ung thư v&uacute; , ung thư buồng trứng, ung thư tử cung v&agrave; ung thư m&aacute;u (bệnh bạch cầu).</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.linhchitienthao.com/wp-content/uploads/2015/12/phong-chong-ung-thu-bang-nam-linh-chi.jpg\" /></p>\r\n\r\n<p>Chất Polysaccharides trong nấm linh chi đỏ gi&uacute;p ph&ograve;ng chống ung thư</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một th&iacute; nghiệm động vật gần đ&acirc;y ở Nhật Bản chỉ ra rằng t&aacute;c dụng của nấm linh chi hữu &iacute;ch trong việc ức chế &nbsp;sự tăng sinh v&agrave; tổn thương hoặc bệnh l&yacute; thay đổi của c&aacute;c tế b&agrave;o ung thư trong ruột kết.</p>\r\n\r\n<p>Một th&iacute; nghiệm trong ống nghiệm được thực hiện bởi &nbsp;Đối t&aacute;c Y tế Clarian, một bệnh viện nổi tiếng ở Mỹ, cũng x&aacute;c nhận rằng nấm linh chi chứa khả năng chinh phục c&aacute;c vitalities của c&aacute;c tế b&agrave;o ung thư v&uacute; v&agrave; tế b&agrave;o c&aacute;c loại ung thư.</p>\r\n\r\n<p>Trong Hội nghị Nghi&ecirc;n Cứu Quốc Tế về nấm ăn tổ chức tại Miami, Mỹ v&agrave;o đầu th&aacute;ng 3 năm 2005, b&aacute;o c&aacute;o nghi&ecirc;n cứu do một nh&agrave; nghi&ecirc;n cứu tại Nhật Bản cũng chỉ ra lợi &iacute;ch của nấm linh chi cho c&aacute;c vấn đề bệnh tuyến tiền liệt.</p>\r\n\r\n<p><img alt=\"phong-chong-ung-thu-bang-nam-linh-chi\" src=\"https://www.linhchitienthao.com/wp-content/uploads/2015/12/bieu-hien-viem-tuyen-tien-liet2.jpg\" /></p>\r\n\r\n<p>Nấm Linh Chi C&oacute; T&aacute;c Dụng Ph&ograve;ng Chống Ung Thư Tuyến Tiền Liệt</p>\r\n\r\n<p>Nghi&ecirc;n cứu &nbsp;cho thấy kết quả chất Triterpenes trong nấm linh chi c&oacute; t&aacute;c dụng rất hữu &iacute;ch trong việc ph&ograve;ng ngừa v&agrave; điều trị ung thư tuyến tiền liệt v&agrave; tuyến tiền liệt lớn th&ocirc;ng qua khả năng của n&oacute; trong điều phục sức sống của 5 alpha &ndash; reductase. (Chiết xuất từ ​​vấn đề thứ 24 của tạp ch&iacute; y khoa &rdquo; Nấm Linh Chi&rdquo;</p>\r\n', 'item_linhchido.jpg', '2019-01-07');

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `news`
--

CREATE TABLE `news` (
  `id` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci,
  `image` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `new_date` date DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `news`
--

INSERT INTO `news` (`id`, `name`, `content`, `image`, `new_date`) VALUES
(1, 'Làm sao để thoát khỏi chứng đau nửa đầu', '<h2><strong><em>Chứng đau nửa đầu hay c&ograve;n gọi l&agrave; đau đầu Migraine l&agrave; một chứng bệnh do căn nguy&ecirc;n mạch m&aacute;u. Đặc điểm cơ bản: Đau đầu từng cơn, khởi ph&aacute;t từ khi c&ograve;n l&agrave; thanh thiếu ni&ecirc;n, nữ gặp nhiều hơn nam v&agrave; đa số c&oacute; yếu tố gia đ&igrave;nh.</em></strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nguy&ecirc;n nh&acirc;n của đau nửa đầu hiện chưa được biết r&otilde; nhưng c&aacute;c nh&agrave; khoa học thường cho rằng bệnh c&oacute; li&ecirc;n quan tới sự co gi&atilde;n đột ngột v&agrave; bất thường của mạch m&aacute;u nhỏ trong n&atilde;o bộ (rối loạn vận mạch n&atilde;o) do t&aacute;c dụng của một số chất trung gian thần kinh như Serotonin theo cơ chế thể dịch hay thần kinh. Ngo&agrave;i ra, c&ograve;n c&oacute; một số yếu tố thuận lợi l&agrave; sự khởi ph&aacute;t của c&aacute;c cơn đau nửa đầu như: Thay đổi thời tiết, căng thẳng, nhiễm tr&ugrave;ng, chế độ ăn...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"business-lawyer-glenroy\" src=\"http://duocphamgiabao.com/upload/news/11494471853555_6551.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Y học hiện đại điều trị chứng đau nửa đầu hiện c&ograve;n nhiều kh&oacute; khăn. C&aacute;c thuốc điều trị bao gồm thuốc cắt cơn (như giảm đau, chống co thắt mạch m&aacute;u n&atilde;o) v&agrave; c&aacute;c thuốc dự ph&ograve;ng như: Thuốc ức chế giao cảm, chống trầm cảm, chống động kinh v&agrave; Corticoid. Nhưng hiệu quả chưa thực sự r&otilde; rệt v&agrave; c&ograve;n nhiều t&aacute;c dụng phụ khi phải điều trị k&eacute;o d&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Y học cổ truyền c&oacute; nhiều b&agrave;i thuốc hay, t&aacute;c dụng tr&ecirc;n nhiều cơ chế bệnh sinh của bệnh l&yacute; đau nửa đầu như: Cải thiện vận mạch, chống co thắt mạch m&aacute;u v&agrave; l&agrave;m tăng sức bền th&agrave;nh mạch... Ngo&agrave;i ra, thuốc y học cổ truyền cũng c&oacute; t&aacute;c dụng điều ho&agrave; sự hoạt động của hệ thống thần kinh từ đ&oacute; l&agrave;m giảm căng thẳng v&agrave; cải thiện t&igrave;nh trạng rối loạn giấc ngủ g&oacute;p phần kh&ocirc;ng nhỏ trong việc kiểm so&aacute;t cơn đau nửa đầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với những ưu điểm như tr&ecirc;n, bệnh nh&acirc;n đau nửa đầu b&aacute;n cấp v&agrave; mạn t&iacute;nh c&oacute; thể được điều trị v&agrave; kiểm so&aacute;t tốt bằng thuốc y học cổ truyền m&agrave; kh&ocirc;ng lo ngại về t&aacute;c dụng phụ hay t&igrave;nh trạng phụ thuộc v&agrave;o thuốc. V&igrave; vậy, lựa chọn y học cổ truyền l&agrave; một biện ph&aacute;p tốt vừa điều trị, vừa gi&uacute;p ph&ograve;ng ngừa t&aacute;i ph&aacute;t của t&igrave;nh trạng đau nửa đầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một trong những chế phẩm ti&ecirc;u biểu của Y học cổ truyền l&agrave; Hoạt huyết Linh Khu&ecirc; Đường. Thực tế tr&ecirc;n l&acirc;m s&agrave;ng, nhiều bệnh nh&acirc;n đ&atilde; c&oacute; đ&aacute;p ứng tốt với Hoạt huyết Gia Bảo chỉ sau khoảng một tuần điều trị. Bệnh nh&acirc;n đ&atilde; cải thiện được hầu hết c&aacute;c triệu chứng như đau đầu, &ugrave; tại, ch&oacute;ng mặt, mất ngủ một c&aacute;ch nhanh ch&oacute;ng. Tiếp tục sử dụng từ 2 đến 3 th&aacute;ng, bệnh tiến triển ổn định, giảm hẳn nguy cơ t&aacute;i ph&aacute;t, chất lượng cuộc sống của bệnh nh&acirc;n được cải thiện r&otilde; rệt.</p>\r\n', 'nhucdau.png', '2019-01-01'),
(2, 'Thái hóa cột sống cổ', '<h2><strong><em>Tho&aacute;i h&oacute;a cột sống cổ l&agrave; t&igrave;nh trạng rất thường gặp ở những người ở độ tuổi trung ni&ecirc;n trở về gi&agrave;. Người bệnh thường cố gắng chịu đựng sự kh&oacute; chịu do tho&aacute;i h&oacute;a cột sống cổ g&acirc;y ra cho đến khi c&aacute;c triệu chứng nặng l&ecirc;n mới t&igrave;m đến thầy thuốc để được tư vấn điều trị.</em></strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tho&aacute;i h&oacute;a cột sống cổ, cũng giống như t&igrave;nh trạng l&atilde;o h&oacute;a của cơ thể ng&agrave;y c&agrave;ng phổ biến ở những người trẻ tuổi do lối sống &iacute;t vận động, ngồi nhiều trước m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh hoặc sử dụng điện thoại di động qu&aacute; nhiều trong tư thế c&uacute;i đầu. Đặc biệt thường gặp ở người l&agrave;m c&ocirc;ng việc văn ph&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://www.duocphamgiabao.com/upload/images/Benh-thoai-hoa-cot-song-co-the-chua-khoi-hoan-toan-duoc-khong-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi mới bị tho&aacute;i h&oacute;a cột sống cổ, biểu hiện chủ yếu l&agrave;: Đau mỏi cổ vai g&aacute;y, co cứng cơ cổ, vận động cổ kh&ocirc;ng thoải m&aacute;i. Mọi người đều chủ quan v&agrave; bỏ qua những triệu chứng tưởng chừng đơn giản n&agrave;y. Qua một thời gian, t&igrave;nh trạng n&agrave;y sẽ nặng l&ecirc;n, bệnh xuất hiện th&ecirc;m c&aacute;c triệu chứng như: Đau đầu v&ugrave;ng cổ g&aacute;y, t&ecirc; b&igrave; cổ vai tay, cứng g&aacute;y, hạn chế vận động cổ. C&oacute; thể k&egrave;m theo rối loạn giấc ngủ, hoa mắt ch&oacute;ng mặt&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để cải thiện t&igrave;nh trạng tho&aacute;i h&oacute;a cột sống cổ, ch&uacute;ng ta phải biết c&aacute;ch tự c&acirc;n bằng giữa c&ocirc;ng việc v&agrave; chế độ nghỉ ngơi hợp l&yacute;. Chẳng hạn như tập một v&agrave;i động t&aacute;c vận động giữa giờ l&agrave;m việc để gi&uacute;p cải thiện t&igrave;nh trạng tắc nghẽn đốt sống, gi&uacute;p lưu th&ocirc;ng tuần ho&agrave;n m&aacute;u v&ugrave;ng cột sống cổ, gi&uacute;p ch&uacute;ng ta tỉnh t&aacute;o hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, ch&uacute;ng ta c&oacute; thể sử dụng một số sản phẩm từ thảo dược hỗ trợ giải quyết những triệu chứng kh&oacute; chịu của t&igrave;nh trạng tho&aacute;i h&oacute;a cột sống cổ g&acirc;y ra. về cơ bản, sản phẩm phải đạt được c&aacute;c ti&ecirc;u ch&iacute; về t&aacute;c dụng như:</p>\r\n\r\n<ul>\r\n	<li>Hoạt huyết h&oacute;a ứ, gi&uacute;p lưu th&ocirc;ng kh&iacute; huyết để cải thiện t&igrave;nh trạng đau đầu, hoa mắt, ch&oacute;ng mặt.</li>\r\n	<li>Thư c&acirc;n, chỉ thống để giảm đau v&agrave; t&ecirc; b&igrave; cổ vai g&aacute;y, giảm co cứng cơ cổ.</li>\r\n	<li>Điều h&ograve;a thần kinh gi&uacute;p cải thiện giấc ngủ.</li>\r\n</ul>\r\n\r\n<p>Hoạt huyết Gia Bảo l&agrave; một chế phẩm từ b&agrave;i thuốc nam của y học cổ truyền, hội tụ đầy đủ đặc t&iacute;nh v&agrave; t&aacute;c dụng như tr&ecirc;n. Đ&acirc;y l&agrave; sản phẩm được c&aacute;c b&aacute;c sĩ bệnh viện Y học cổ truyền Trung Ương nghi&ecirc;n cứu v&agrave; thử nghiệm nhiều năm tr&ecirc;n l&acirc;m s&agrave;ng từ b&agrave;i thuốc được sử dụng l&acirc;u đời h&agrave;ng ng&agrave;n năm. Hoạt huyết Gia Bảo kh&ocirc;ng những gi&uacute;p ch&uacute;ng ta tho&aacute;t khỏi những phiền to&aacute;i của t&igrave;nh trạng tho&aacute;i h&oacute;a cột sống cổ m&agrave; c&ograve;n an to&agrave;n, kh&ocirc;ng c&oacute; t&aacute;c dụng phụ.</p>\r\n', 'thaihoa.jpg', '2019-01-02'),
(3, 'Sỏi tiết niệu', '<h2><em>Sỏi tiết niệu (sỏi thận, sỏi đ&agrave;i bể thận, sỏi niệu quản, sỏi b&agrave;ng quang) l&agrave; một trong những bệnh l&yacute; rất phổ biến hiện nay, kh&oacute; điều trị dứt điểm v&agrave; thường hay t&aacute;i ph&aacute;t.</em>&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; &nbsp;nhiều nguy&ecirc;n nh&acirc;n phối hợp g&acirc;y bệnh như: Chế độ ăn uống nhiều calci, nhiều acid uric ,do nhiễm khuẩn (sỏi struvit); do bệnh to&agrave;n th&acirc;n như cường cận gi&aacute;p (sỏi calci), loạn dưỡng cystin, oxalic &hellip;cũng như do giới t&iacute;nh, m&ocirc;i trường sống, lối sống v&agrave; t&igrave;nh trạng nghề nghiệp.Sỏi thận, sỏi tiết niệu thường tiến triển &acirc;m thầm, ch&uacute;ng ta thường t&igrave;nh cờ ph&aacute;t hiện bệnh khi đi kiểm tra sức khỏe hoặc chỉ biết khi đ&atilde; c&oacute; c&aacute;c triệu chứng như :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><strong>&nbsp; &nbsp; &nbsp;* Đau</strong></em></p>\r\n\r\n<ul>\r\n	<li>Cơn đau dữ dội, được gọi l&agrave;&nbsp; &ldquo;cơn đau quặn thận&rdquo;. Đau thường khởi ph&aacute;t từ c&aacute;c điểm niệu quản, lan dọc xuống g&ograve; mu, c&oacute; khi đau lan cả ra h&ocirc;ng, lưng. C&oacute; thể k&egrave;m theo buồn n&ocirc;n, n&ocirc;n.</li>\r\n	<li>Đau &acirc;m ỉ: gặp ở sỏi vừa v&agrave; nhỏ, nằm ở vị tr&iacute; bể thận hoặc khi sỏi nhỏ di chuyển trong đường tiết niệu. Đau c&oacute; thể k&egrave;m b&iacute; đ&aacute;i do sỏi đ&atilde; b&iacute;t tắc ở cổ b&agrave;ng quang hoặc lọt ra niệu đạo</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp;<strong>&nbsp;*&nbsp;<em>Đ&aacute;i m&aacute;u</em>:</strong>&nbsp;c&oacute; thể l&agrave; vi thể hoặc đại thể, đ&acirc;y l&agrave; biến chứng thường gặp của sỏi tiết niệu, nhất l&agrave; khi sỏi đang di chuyển g&acirc;y tổn thương niệu quản.</p>\r\n\r\n<p>&nbsp; &nbsp; *&nbsp;<strong><em>Đ&aacute;i buốt, đ&aacute;i rắt, đ&aacute;i mủ</em></strong>: xảy ra khi c&oacute; nhiễm khuẩn tiết niệu.</p>\r\n\r\n<p>&nbsp; &nbsp; *&nbsp;<strong>Sốt:</strong>&nbsp;c&oacute; thể sốt cao, r&eacute;t run k&egrave;m theo c&aacute;c triệu chứng tr&ecirc;n l&agrave; dấu hiệu của vi&ecirc;m thận-bể thận cấp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Về điều trị, t&ugrave;y theo loại sỏi v&agrave; k&iacute;ch thước, số lượng v&agrave; vị tr&iacute; của sỏi m&agrave; sẽ c&oacute; c&aacute;c phương ph&aacute;p ph&ugrave; hợp như điều trị nội khoa hay phải can thiệp ngoại khoa. Do sỏi tiết niệu thường diễn biến &acirc;m thầm, chỉ được ph&aacute;t hiện khi đ&atilde; c&oacute; tắc nghẽn đường tiểu g&acirc;y đau hoặc t&igrave;nh cờ ph&aacute;t hiện khi kh&aacute;m sức khỏe. Nếu kh&ocirc;ng được ph&aacute;t hiện v&agrave; giải quyết kịp thời c&oacute; thể g&acirc;y n&ecirc;n c&aacute;c cơn đau quặn thận cấp, hoặc nguy hiểm hơn l&agrave; suy thận, do vậy việc ph&aacute;t hiện v&agrave; điều trị ph&ugrave; hợp l&agrave; rất quan trọng.&nbsp; Việc điều trị cũng n&ecirc;n được c&acirc;n nhắc từ điều trị nội khoa trước khi điều trị ngoại khoa. Rất nhiều trường hợp sỏi tiết niệu c&oacute; thể điều trị ổn định, thậm ch&iacute; khỏi hẳn nhờ Y học cổ truyền, chưa kể đến c&ograve;n c&oacute; thể gi&uacute;p giảm thiểu nguy cơ t&aacute;i ph&aacute;t v&agrave; n&acirc;ng cao sức khỏe to&agrave;n trạng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo YHCT, soi thận được quy v&agrave;o chứng Thạch L&acirc;m. Nguy&ecirc;n nh&acirc;n l&agrave; do sự mất c&acirc;n bằng của c&aacute;c tạng phủ trong cơ thể g&acirc;y n&ecirc;n sự mất c&acirc;n bằng của c&aacute;c qu&aacute; tr&igrave;nh vận h&oacute;a c&aacute;c chất v&agrave; đ&agrave;o thải cặn b&atilde;, từ đ&oacute; sinh ra sỏi. Theo kinh nghiệm từ h&agrave;ng ngh&igrave;n năm nay, đ&atilde; c&oacute; rất nhiều b&agrave;i thuốc qu&yacute; ho&agrave;n to&agrave;n từ thuốc thảo dược gi&uacute;p điều trị v&agrave; hỗ trợ điều trị rất hiệu quả sỏi k&iacute;ch thước nhỏ đến vừa, cũng như ph&ograve;ng ngừa bệnh. Kh&ocirc;ng kh&oacute; để t&igrave;m thấy những ca l&acirc;m s&agrave;ng sau khi sử dụng thuốc, k&iacute;ch thước sỏi nhỏ lại v&agrave; bệnh nh&acirc;n c&oacute; thể đi tiểu ra sỏi.</p>\r\n', 'soitietnieu.jpg', '2019-01-03'),
(4, 'Tắc tia sữa - Nỗi khổ của các bà mẹ sau sinh', '<h2>Tắc tia sữa l&agrave; hiện tượng người mẹ sau sinh tia sữa bị tắc, sữa ứ lại v&agrave; kh&ocirc;ng ra ngo&agrave;i được. Dần dần, l&acirc;u ng&agrave;y sẽ h&igrave;nh th&agrave;nh cục, h&ograve;n sữa kết tinh g&acirc;y đau đớn. Nếu kh&ocirc;ng giải quyết kịp thời c&oacute; thể g&acirc;y ap xe v&uacute;, nguy hiểm cho người mẹ.</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; lẽ chỉ c&oacute; ai đ&atilde; trải qua mới hiểu cảm gi&aacute;c đau nhức khủng khiếp khi bị vi&ecirc;m tắc tia sữa. Đ&acirc;y l&agrave; t&igrave;nh trạng bệnh l&yacute; kh&ocirc;ng hề hiếm gặp ở phụ nữ sau sinh, c&oacute; thể xảy ra ở bất k&igrave; thời điểm n&agrave;o trong giai đoạn cho con b&uacute;. Sự tiết sữa của người mẹ diễn ra b&igrave;nh thường phụ thuộc v&agrave;o nhiều yếu tố như: Sức khỏe thể chất cũng như tinh thần của người mẹ, động t&aacute;c m&uacute;t của con, nhiễm khuẩn do ứ đọng sữa thừa b&eacute; kh&ocirc;ng b&uacute; hết, kh&ocirc;ng vệ sinh n&uacute;m v&uacute;, tổn thương do ngoại cảnh như chấn thương hoặc mặc &aacute;o ngực qu&aacute; chật,... Vậy n&ecirc;n, mỗi yếu tố tr&ecirc;n thay đổi th&igrave; đều c&oacute; thể ảnh hưởng đến sự tiết sữa. Do đ&oacute;, c&aacute;c b&agrave; mẹ c&oacute; thể ph&ograve;ng tr&aacute;nh t&igrave;nh trạng n&agrave;y bằng c&aacute;ch trang bị thật tốt c&aacute;c kiến thức li&ecirc;n quan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://www.duocphamgiabao.com/upload/images/kho-chiu-vi-bi-tac-tia-sua-lam-sao-de-chua-tri.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n đ&ocirc;i khi c&oacute; nhiều yếu tố nguy cơ kh&ocirc;ng thể kiểm so&aacute;t hết, cho n&ecirc;n vi&ecirc;m tắc tia sữa vẫn l&agrave; vấn đề đau đầu, mang tới nhiều mệt mỏi cho kh&ocirc;ng &iacute;t c&aacute;c b&agrave; mẹ. N&oacute; kh&ocirc;ng chỉ ảnh hưởng tới tinh thần v&agrave; thể chất của người mẹ m&agrave; c&ograve;n khiến trẻ kh&ocirc;ng thể b&uacute;, do đ&oacute; &iacute;t nhiều ảnh hưởng đến t&igrave;nh trạng dinh dưỡng của b&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Biểu hiện của bệnh kh&aacute; r&otilde; r&agrave;ng, người mẹ thường thấy một hoặc hai v&uacute; căng to, tức v&agrave; đau nhức, c&agrave;ng l&uacute;c c&agrave;ng tăng dần. Sờ v&uacute; th&igrave; thấy c&aacute;c khối tr&ograve;n di động nằm ri&ecirc;ng rẽ hoặc hợp th&agrave;nh một khối lớn, cứng v&agrave; rất đau. C&oacute; thể k&egrave;m theo sốt nhẹ, mệt mỏi, nhức đầu,... sữa kh&ocirc;ng tiết được ra khi b&eacute; b&uacute;, nặn v&agrave; thậm ch&iacute; d&ugrave;ng m&aacute;yh&uacute;t sữa.Nguy&ecirc;n tắc điều trị cơ bản l&agrave; l&agrave;m th&ocirc;ng tia sữa, chống vi&ecirc;m v&agrave; kh&aacute;ng sinh nếu c&oacute; nhiễm khuẩn. Nhưng việc l&agrave;m th&ocirc;ng tia sữa bằng c&aacute;c phương ph&aacute;p như nắn, xoa b&oacute;p, h&uacute;t để loại bỏ sữa thừa thường g&acirc;y đau đớn v&agrave; căng thẳng rất nhiều cho người mẹ, do đ&oacute; phần n&agrave;o l&agrave;m cho việc cơ thể điều tiết để tiết sữa c&agrave;ng kh&oacute; khăn hơn. C&oacute; thể thấy, tắc tia sữa li&ecirc;n quan kh&aacute; mật thiết đến t&igrave;nh trạng sức khỏe của người mẹ, thuốc YHCT c&oacute; hiệu quả cao trong việc n&acirc;ng cao sức khỏe to&agrave;n trạng, c&acirc;n bằng hoạt động của c&aacute;c tạng phủ trong cơ thể,từ đ&oacute; c&acirc;n bằng qu&aacute; tr&igrave;nh tiết sữa m&agrave; kh&ocirc;ng g&acirc;y ra t&aacute;c dụng kh&ocirc;ng mong muốn n&agrave;o. Điều n&agrave;y rất quan trọng v&igrave; đ&acirc;y l&agrave; giai đoạn nhạy cảm, việc điều trị nếu kh&ocirc;ng ch&uacute; &yacute; sẽ ảnh hưởng đến số lượng v&agrave; chất lượng sữa mẹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc d&ugrave;ng YHCT v&agrave;o điều trị tắc tia sữa đ&atilde; ghi nhận rất nhiều hiệu quả t&iacute;ch cực. Bệnh nh&acirc;n được sử dụng ho&agrave;n to&agrave;n bằng thuốc YHCT v&agrave; ch&acirc;m cứu cũng như massage nhẹ nh&agrave;ng, m&agrave; kh&ocirc;ng g&acirc;y t&aacute;c dụng phụ cho mẹ cũng nhưng kh&ocirc;ng ảnh hưởng đến chất lượng sữa.</p>\r\n', 'khochiuvibitactiasualamsaodechuatri_1766.jpg', '2019-01-04'),
(5, 'Biểu hiện của bệnh Thận yếu', '<p>Bệnh thận yếu nếu kh&ocirc;ng được ph&aacute;t hiện v&agrave; điều trị kịp thời sẽ ảnh hưởng rất nghi&ecirc;m trọng đến những chức năng kh&aacute;c trong cơ thể.&nbsp;Jeremy Allen, DO &ndash; một th&agrave;nh vi&ecirc;n thuộc hội đồng Chăm s&oacute;c gia đ&igrave;nh Mỹ cho biết: D&ugrave; &ldquo;hầu như kh&ocirc;ng c&oacute; triệu chứng nhưng bệnh nh&acirc;n vẫn c&oacute; thể mất đến 90% chức năng thận khi ph&aacute;t hiện ra bệnh&rdquo;.</p>\r\n\r\n<h3>Khi c&oacute; một số dấu hiệu dưới đ&acirc;y th&igrave; bạn n&ecirc;n đi kh&aacute;m ngay v&igrave; c&oacute; thể bạn đang mắc phải bệnh thận yếu.</h3>\r\n\r\n<h3><strong>Uống nước b&igrave;nh thường nhưng đi tiểu &iacute;t hơn.</strong></h3>\r\n\r\n<p>Khi thận yếu, chất lỏng bị t&iacute;ch lại trong m&ocirc; thay v&igrave; được thải khỏi cơ thể, n&ecirc;n bạn c&oacute; thể sẽ đi tiểu &iacute;t hơn.</p>\r\n\r\n<h3><strong>Thiếu m&aacute;u, mệt mỏi.</strong></h3>\r\n\r\n<p>Thận cũng c&oacute; chức năng điều chỉnh nồng độ hemoglobin n&ecirc;n khi thận hoạt động k&eacute;m, bạn c&oacute; thể bị thiếu m&aacute;u, mệt mỏi, cảm thấy lu&ocirc;n buồn ngủ.</p>\r\n\r\n<h3><strong>Ch&aacute;n ăn, kh&oacute; tập trung suy nghĩ.</strong></h3>\r\n\r\n<p>Những vấn đề n&agrave;y xảy ra khi chất thải bị t&iacute;ch tụ lại trong cơ thể v&agrave; bắt đầu g&acirc;y rối loạn c&aacute;c bộ phận kh&aacute;c tr&ecirc;n cơ thể bao gồm cả dạ d&agrave;y v&agrave; n&atilde;o.</p>\r\n\r\n<h3><strong>Tăng huyết &aacute;p cũng l&agrave; biểu hiện của bệnh thận yếu.</strong></h3>\r\n\r\n<p>Một khi chức năng thận bị suy giảm, ch&uacute;ng kh&ocirc;ng thể kiểm so&aacute;t huyết &aacute;p hiệu quả nữa. &Aacute;p lực m&aacute;u trong th&agrave;nh mạch tăng l&ecirc;n lại c&agrave;ng g&acirc;y hại cho mạch m&aacute;u chảy đến thận, khiến thận ng&agrave;y c&agrave;ng suy yếu hơn.</p>\r\n\r\n<h3><strong>Nhịp tim bất thường.</strong></h3>\r\n\r\n<p>Thận hoạt động k&eacute;m g&acirc;y t&iacute;ch tụ kali khiến nhịp tim trở n&ecirc;n rối loạn, bất thường.</p>\r\n\r\n<h3><strong>Những người dễ bị bệnh thận yếu:</strong></h3>\r\n\r\n<p>&ndash; Những người thường xuy&ecirc;n h&uacute;t thuốc, uống rượu.</p>\r\n\r\n<p>&ndash; Những người c&oacute; cuộc sống sinh hoạt v&agrave; ăn uống thường ng&agrave;y kh&ocirc;ng đ&uacute;ng quy luật.</p>\r\n\r\n<p>&ndash; Người l&agrave;m việc bận rộn, tinh thần căng thẳng.</p>\r\n\r\n<p>&ndash; Người th&iacute;ch uống tr&agrave; đặc.</p>\r\n\r\n<p>&ndash; Người l&agrave;m việc b&ecirc;n m&aacute;y t&iacute;nh thời gian d&agrave;i.</p>\r\n\r\n<p>&ndash; Người bệnh đang trong thời kỳ hồi phục.</p>\r\n\r\n<p>&ndash; Người hay ngồi l&acirc;u trong thời gian d&agrave;i.</p>\r\n\r\n<p>&ndash; Người l&agrave;m &ldquo;chuyện ấy&rdquo; qu&aacute; thường xuy&ecirc;n.</p>\r\n\r\n<p>&ndash; Người hay uống thuốc tr&aacute;ng dương.</p>\r\n\r\n<p>&ndash; Người gi&agrave;.</p>\r\n\r\n<p><em>C&oacute; thể những th&oacute;i quen hằng ng&agrave;y tưởng chừng như l&agrave; v&ocirc; hại tuy nhi&ecirc;n n&oacute; lại &acirc;m thầm &ldquo;ph&aacute; hủy&rdquo; cơ thể bạn. Nếu đang gặp phải những biểu hiện tương tự n&ecirc;u tr&ecirc;n bạn h&atilde;y nhanh ch&oacute;ng đi kh&aacute;m hoặc li&ecirc;n hệ c&aacute;c b&aacute;c sĩ để được tư vấn kịp thời.</em></p>\r\n', 'than.jpg', '2019-01-05'),
(6, 'Điều trị hiệu quả hen phế quản bằng Y học cổ truyền', '<h2><strong><em>HEN PHẾ QUẢN</em></strong><em>&nbsp;l&agrave; một bệnh l&yacute; vi&ecirc;m mạn t&iacute;nh của phế quản , c&oacute; rất nhiều nguy&ecirc;n nh&acirc;n kết hợp g&acirc;y n&ecirc;n t&igrave;nh trạng tăng đ&aacute;p ứng phế quản,dẫn đến những đợt t&aacute;i diễn của ran r&iacute;t, kh&oacute; thở, b&oacute; s&aacute;t lồng ngực v&agrave; ho, đặc biệt xảy ra ban đ&ecirc;m hay v&agrave;o s&aacute;ng sớm; li&ecirc;n quan nhiều đến khả năng miễn dịch của cơ thể, xảy ra ở mọi lứa tuổi, thường gặp ở trẻ em nhiều hơn.</em></h2>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Nguy&ecirc;n nh&acirc;n thường do:</p>\r\n\r\n<ul>\r\n	<li><strong>Hen phế quản dị ứng kh&ocirc;ng nhiễm khuẩn:</strong>nguy&ecirc;n nh&acirc;n l&agrave; do dị ứng c&aacute;c ứng nguy&ecirc;n h&ocirc; hấp như l&agrave; bụi nh&agrave;, c&aacute;c loại bọ nh&agrave;,bụi chăn đệm, c&aacute;c l&ocirc;ng m&oacute;ng c&aacute;c lo&agrave;i gia s&uacute;c như ch&oacute;, m&egrave;o, chuột, thỏ, &nbsp;phấn hoa, c&acirc;y cỏ, hay nghề nghiệp trong c&aacute;c xưởng dệt hoặc dị ứng c&aacute;c loại &nbsp;thuốc, h&oacute;a chất.</li>\r\n	<li><strong>Hen phế quản dị ứng nhiễm khuẩn:</strong>Vi khuẩn, virus, nấm,&hellip;</li>\r\n	<li><strong>Hen phế quản kh&ocirc;ng do dị ứng:</strong></li>\r\n	<li>&nbsp;Di truyền:&nbsp;Tiền sử gia đ&igrave;nh, li&ecirc;n quan đến kh&aacute;ng nguy&ecirc;n h&ograve;a hợp tổ chức HLA.</li>\r\n	<li>Gắng sức:&nbsp;Khi gắng sức v&agrave; nhất l&agrave; khi ngưng gắng sức.</li>\r\n	<li>Thời tiết: Kh&ocirc;ng kh&iacute; lạnh.</li>\r\n	<li>Rối loạn nội tiết: Trong thời kỳ trưởng th&agrave;nh, thời kỳ trước kinh nguyệt, l&uacute;c mang thai, thời kỳ m&atilde;n kinh.</li>\r\n	<li>Yếu tố t&acirc;m l&yacute;: T&acirc;m trạng lo &acirc;u, m&acirc;u thuẫn cảm x&uacute;c, chấn thương t&igrave;nh cảm.</li>\r\n</ul>\r\n\r\n<p>​</p>\r\n\r\n<p>Do c&aacute;c nguy&ecirc;n nh&acirc;n rất đa dạng v&agrave; bệnh thường c&oacute; xu hướng tiến triển m&atilde;n t&iacute;nh, dai dẳng, n&ecirc;n việc điều trị hen phế quản cũng gặp kh&aacute; nhiều kh&oacute; khăn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bệnh nh&acirc;n mắc bệnh n&agrave;y thường c&oacute; chất lượng cuộc sống k&eacute;m, khi c&oacute; c&aacute;c&nbsp; cơn hen cấp t&iacute;nh g&acirc;y ra t&igrave;nh trạng kh&oacute; thở nặng, rất nguy hiểm nếu kh&ocirc;ng được cấp cứu kịp thời. Với c&aacute;c trường hợp bệnh m&atilde;n t&iacute;nh th&igrave; bệnh nh&acirc;n lu&ocirc;n mệt mỏi, phải điều trị thuốc cả đời v&agrave; cần phải c&oacute; chế độ tập luyện ki&ecirc;ng khem khắt khe nếu kh&ocirc;ng muốn bệnh t&aacute;i ph&aacute;t đợt cấp. Chưa kể t&aacute;c dụng phụ nặng nề do việc sử dụng c&aacute;c thuốc chống vi&ecirc;m v&agrave; d&atilde;n phế quản l&acirc;u ng&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện nay T&acirc;y y c&oacute; rất nhiều loại thuốc điều trị hen phế quản, nhưng phần lớn chỉ sử dụng trong c&aacute;c đợt cấp, gi&uacute;p điều trị triệu chứng , kh&ocirc;ng mang lại hiệu quả l&acirc;u d&agrave;i m&agrave; lại g&acirc;y n&ecirc;n nhiều t&aacute;c dụng phụ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khắc phục được những vấn đề tr&ecirc;n, ph&ograve;ng kh&aacute;m&nbsp;<strong>Đ&ocirc;ng Y Linh Khu&ecirc; Đường</strong>&nbsp;ch&uacute;ng t&ocirc;i, với đội ngũ b&aacute;c sĩ c&oacute; nhiều năm kinh nghiệm trong việc sử dụng YHCT kết hợp với nhiều phương ph&aacute;p ti&ecirc;n tiến v&agrave;o điều trị, mang đến những giải ph&aacute;p tuyệt vời kh&aacute;c thực sự gi&uacute;p điều trị v&agrave;o nguy&ecirc;n nh&acirc;n g&acirc;y bệnh, kh&ocirc;ng những gi&uacute;p cải thiện c&aacute;c triệu chứng m&agrave; c&ograve;n khiến cho sức khỏe to&agrave;n th&acirc;n được n&acirc;ng cao. Từ đ&oacute; mang lại những tia hi vọng mới cho c&aacute;c bệnh nh&acirc;n Hen Phế Quản.</p>\r\n', 'benhhenphequanlagitrieuchungvacachphongbenh1_8390.jpg', '2019-01-07'),
(7, ' 8 dấu hiệu nhận biết khi mắc các bệnh về Tim mạch', '<p>Bệnh tim l&agrave; một trong những nguy&ecirc;n nh&acirc;n g&acirc;y tử vong h&agrave;ng đầu tr&ecirc;n thế giới, nhưng điều nguy hiểm l&agrave; nhiều người lại kh&ocirc;ng hề nhận biết được dấu hiệu của bệnh do n&oacute; đến rất &acirc;m thầm.</p>\r\n\r\n<p><strong>1. Mệt mỏi bất th&igrave;nh l&igrave;nh</strong></p>\r\n\r\n<p>Bạn đột nhi&ecirc;n kh&ocirc;ng thể đi bộ một qu&atilde;ng đường trung b&igrave;nh hoặc kh&ocirc;ng thể nhấc một m&oacute;n đồ l&ecirc;n cầu thang dễ d&agrave;ng như trước kia... V&agrave; sau một đ&ecirc;m nghỉ ngơi, t&igrave;nh trạng n&agrave;y vẫn kh&ocirc;ng cải thiện th&igrave; bạn n&ecirc;n đi kh&aacute;m ngay lập tức.</p>\r\n\r\n<p><strong>2. Vấn đề t&igrave;nh dục</strong></p>\r\n\r\n<p>Kh&ocirc;ng thể cương dương ở nam giới b&aacute;o hiệu động mạch dẫn đến bộ phận sinh dục đ&atilde; bị tắc nghẽn. Đồng thời động mạch đến tim cũng đ&atilde; c&oacute; thể bị nghẽn. T&igrave;nh trạng bất lực thường xảy ra trước khi đột quỵ v&agrave;i năm.</p>\r\n\r\n<p><strong>3. Huyết &aacute;p cao</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một trong những nguy&ecirc;n nh&acirc;n ch&iacute;nh g&acirc;y trụy tim. Huyết &aacute;p cao k&eacute;o d&agrave;i g&acirc;y hại cho động mạch, khiến mảng b&aacute;m t&iacute;ch tụ ở th&agrave;nh động mạch.</p>\r\n\r\n<p>Mọi người thường chỉ nhận ra huyết &aacute;p cao khi chỉ số đ&atilde; cao qu&aacute; mức, n&ecirc;n người c&oacute; nguy cơ n&ecirc;n kiểm tra huyết &aacute;p thường xuy&ecirc;n.</p>\r\n\r\n<p><strong>4. Ho l&acirc;u d&agrave;i</strong></p>\r\n\r\n<p>Nếu bị ho khi nằm xuống, ngừng ho khi ngồi dậy, đ&acirc;y l&agrave; dấu hiệu bạn bị suy tim xung huyết. Dung dịch bắt đầu t&iacute;ch tụ trong phổi khi tim kh&ocirc;ng đập đ&uacute;ng c&aacute;ch, dồn ngược lại mạch m&aacute;u, khiến dịch chảy v&agrave;o những nơi bất thường.</p>\r\n\r\n<p><strong>5. Kh&oacute; thở khi ngủ</strong></p>\r\n\r\n<p>Nếu bị tỉnh giấc giữa đ&ecirc;m, cảm thấy kh&oacute; thở, ngạt hơi, bạn c&oacute; thể bị nguy cơ cao trụy tim hoặc rung t&acirc;m nhĩ. Bệnh nh&acirc;n bị chứng kh&oacute; thở khi ngủ th&igrave; lượng oxy giảm xuống, mạch m&aacute;u co chặt để tăng cường oxy đến n&atilde;o v&agrave; tim, g&acirc;y ra hơi thở ngắn.</p>\r\n\r\n<p><strong>6. Ch&acirc;n sưng tấy</strong></p>\r\n\r\n<p>Nếu nhận thấy mắt c&aacute; ch&acirc;n trở n&ecirc;n to hơn, c&oacute; vấn đề kh&oacute; xỏ ch&acirc;n v&agrave;o đ&ocirc;i gi&agrave;y quen thuộc, c&oacute; thể đ&acirc;y l&agrave; dấu hiệu tim của bạn c&oacute; vấn đề.</p>\r\n\r\n<p>Khi cơ tim bắt đầu suy, tim kh&ocirc;ng thể bơm m&aacute;u hiệu quả. C&aacute;c tĩnh mạch tắc lại, bơm th&ecirc;m nhiều dịch v&agrave;o m&ocirc; cơ thể, khiến c&aacute;c phần như b&agrave;n ch&acirc;n, bụng sưng l&ecirc;n bất thường. Bạn c&oacute; thể ấn thử l&ecirc;n da, nếu vết ấn tồn tại l&acirc;u chứng tỏ m&ocirc; cơ đang chứa nhiều dịch thừa.</p>\r\n\r\n<p><strong>7. Sưng lợi</strong></p>\r\n\r\n<p>Lợi chảy m&aacute;u hoặc sưng bởi chứng sưng vi&ecirc;m c&oacute; thể l&agrave;m tăng nguy cơ trụy tim. Những người bị bệnh nha chu thường bị sưng vi&ecirc;m nặng, chứng nhiễm tr&ugrave;ng m&atilde;n t&iacute;nh khiến hệ miễn dịch của cơ thể k&iacute;ch hoạt li&ecirc;n tục, c&oacute; thể g&acirc;y thủng lỗ động mạch v&agrave; l&agrave;m rối loạn c&aacute;c mảng b&aacute;m, dẫn đến đột quỵ.</p>\r\n\r\n<p><strong>8. Tim đập nhanh</strong></p>\r\n\r\n<p>Nếu tim đập bất thường hoặc đập qu&aacute; nhanh, c&oacute; thể đ&acirc;y l&agrave; dấu hiệu tim bắt đầu yếu đi.</p>\r\n\r\n<p>Loạn nhịp tim l&agrave; t&igrave;nh trạng bất thường về tim c&oacute; thể g&acirc;y n&ecirc;n bệnh tim sau đ&oacute; nếu kh&ocirc;ng được chữa trị. Đ&acirc;y cũng c&oacute; thể l&agrave; dấu hiệu van tim k&eacute;m, cần đi kh&aacute;m để chẩn đo&aacute;n r&otilde; nguy&ecirc;n nh&acirc;n.&nbsp;</p>\r\n', 'timmach_2010.jpg', '2019-01-08'),
(8, 'Thảo dược - cải thiện tiểu đường', '<p><strong>Một số loại thảo mộc v&agrave; gia vị kh&ocirc;ng chỉ gi&uacute;p cho bữa ăn trở n&ecirc;n tr&ograve;n vị hơn m&agrave; c&ograve;n c&oacute; t&aacute;c dụng cải thiện bệnh tiểu đường. Đương nhi&ecirc;n chỉ n&ecirc;n dừng ở mức vừa phải.</strong></p>\r\n\r\n<p><strong>Tr&agrave; hoa c&uacute;c</strong>: C&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; đưa ra nhiều bằng chứng cho thấy tr&agrave; hoa c&uacute;c c&oacute; t&aacute;c dụng rất tốt cho bệnh nh&acirc;n tiểu đường, dựa v&agrave;o việc thử ngiệm trong ống nghiệm về hoạt t&iacute;nh kh&aacute;ng khuẩn v&agrave; khả năng ngăn ngừa c&aacute;c tiều cầu v&oacute;n cục h&igrave;nh th&agrave;nh m&aacute;u đ&ocirc;ng của tr&agrave; hoa c&uacute;c. B&ecirc;n cạnh vai tr&ograve; tuyệt vời tr&ecirc;n, gần đ&acirc;y c&aacute;c nh&agrave; khoa học c&ograve;n cho biết t&agrave; hoa c&uacute;c c&ograve;n c&oacute; c&ocirc;ng dụng l&agrave;m giảm lượng đường trong m&aacute;u v&agrave; ngăn ngừa biến chứng tiểu đường bằng c&aacute;ch lấy đường ra khỏi m&aacute;u v&agrave; đưa ra một phần v&agrave;o lưu trữ gan.</p>\r\n\r\n<p><strong>Quế</strong>: Trong quế c&oacute; chứa một số hợp chất gi&uacute;p insulin hoạt động hiệu quả , n&acirc;ng cao khả năng vận chuyển glucose tới những tế b&agrave;o đang &ldquo; đ&oacute;i đường&rdquo; . Ngo&agrave;i ra, quế c&ograve;n c&oacute; thể gi&uacute;p tế b&agrave;o chất b&eacute;o nhận dạng v&agrave; phản ứng tốt với insulin. B&ecirc;n cạnh đ&oacute;, một ph&acirc;n t&iacute;ch gần đ&acirc;y cho thấy quế c&ograve;n c&oacute; t&aacute;c dụng l&agrave;m giảm nồng độ Alc hemoglobin trong m&aacute;u (Hemoglobin Glycosylated viết tắt l&agrave; HbAlc, l&agrave; t&ecirc;n Tiếng Anh của thước đo mức độ trung b&igrave;nh trong 2-3 th&aacute;ng theo d&otilde;i)</p>\r\n\r\n<p><strong>Th&igrave; l&agrave;</strong>: Một số nghi&ecirc;n cứu chứng minh rằng th&igrave; l&agrave; l&agrave;m giảm lượng đường trong m&aacute;u v&agrave; kh&aacute;ng insulin ở những người bị bệnh tiểu đường.</p>\r\n\r\n<p><strong>Bồ c&ocirc;ng anh</strong>: Một lợi &iacute;ch sức khỏe tuyệt vời của bồ c&ocirc;ng anh l&agrave; k&iacute;ch th&iacute;ch tuyến tụy sản xuất insulin. Bồ c&ocirc;ng anh l&agrave; một thuốc lợi tiểu tự nhi&ecirc;n. N&oacute; khiến người d&ugrave;ng đi tiểu thường xuy&ecirc;n hơn, gi&uacute;p loại bỏ lượng đường dư thừa ra khỏi cơ thể.</p>\r\n\r\n<p><strong>Tỏi</strong>: Chứa một lượng lớn chất chống vi&ecirc;m c&oacute; t&aacute;c dụng trấn &aacute;p mức cytokine trong cơ thể. . C&aacute;c nh&agrave; nghi&ecirc;n cứu ph&aacute;t hiện ở người mắc tiểu đường dạng 2, nồng độ cytokine được n&acirc;ng cao trong c&aacute;c m&ocirc; mỡ. Mỡ g&acirc;y ra mức độ vi&ecirc;m bất thường v&agrave; k&eacute;o d&agrave;i, l&agrave;m thay đổi t&aacute;c dụng của insulin v&agrave; khi&ecirc;n cho bệnh tiểu đường trầm trọng hơn. Theo&nbsp;<em>Prevention,&nbsp;</em>một cuộc th&iacute; nghiệm tr&ecirc;n động vật gần đ&acirc;y cho thấy tỏi kiểm so&aacute;t rất tốt lượng đường trong m&aacute;u. Sau 2 giờ cho c&aacute;c con vật tham gia th&iacute; nghiệm d&ugrave;ng tỏi, c&aacute;c nh&agrave; khoa học kiểm tra mức đường huyết v&agrave; nhận thấy tỏi l&agrave;m giảm từ 17,9 &ndash; 26,2% nồng độ đường trong m&aacute;u của ch&uacute;ng</p>\r\n\r\n<p><strong>Gừng</strong>: C&oacute; rất nhiều nghi&ecirc;n cứu xung quanh t&aacute;c dụng chữa bệnh của gừng, v&agrave; gần đ&acirc;y một nh&oacute;m b&aacute;c sỹ Iran đ&atilde; ph&aacute;t hiện gừng c&oacute; khả năng điều trị bệnh tiểu đường dạng 2. Đồng thời, một nh&oacute;m nghi&ecirc;n cứu đến từ Trường đại học Y dược &Ocirc;n Ch&acirc;u ( Trung Quốc ) cũng tuy&ecirc;n bố hợp chất curcumin trong gừng, sau khi được biến đổi kết cấu sẽ c&oacute; t&aacute;c dụng ngăn ngừa v&agrave; điều trị biến chứng tiểu đường. Chiết xuất từ gừng c&oacute; thể l&agrave;m tăng hấp thu Glucose v&agrave;o tế b&agrave;o một c&aacute;ch độc lập với t&aacute;c động của insulin. Chiết xuất n&agrave;y c&oacute; t&aacute;c dụng gi&uacute;p cớ thể hấp thu glucose m&agrave; kh&ocirc;ng cần đến sự hoạt động của insulin. B&ecirc;n cạnh đ&oacute; glycogen, một enzyme được tiết ra&nbsp; từ gan c&oacute; thể l&agrave;m tăng lượng đường trong m&aacute;u, sẽ bị ức chế nếu tăng thu nạp gừng hay c&aacute;c chế phẩm từ gừng.</p>\r\n\r\n<p><strong>Nghệ</strong>: Nhiều nghi&ecirc;n cứu đ&atilde; chứng minh nghệ rất l&yacute; tưởng cho những người tiểu đường v&igrave; n&oacute; c&oacute; t&aacute;c dụng chống vi&ecirc;m, chống l&atilde;o h&oacute;a, chống oxy h&oacute;a, bảo vệ thần kinh , chống xơ vữa động mạch. Tất cả những lợi &iacute;ch n&agrave;y đều do th&agrave;nh phần ch&iacute;nh c&oacute; trong củ nghệ l&agrave; curcumin. Một nghi&ecirc;n cứu l&acirc;m s&agrave;ng gần đ&acirc;y của Hiệp hội Tiểu đường Mỹ được xuất bản tr&ecirc;n tạp ch&iacute; Diabetes Care, cho thấy curcumin chiết xuất từ nghệ gi&uacute;p bệnh nh&acirc;n tiền tiểu đường chống ph&aacute;t bệnh sau khi điều trị 9 th&aacute;ng với tỷ lệ th&agrave;nh c&ocirc;ng 100%</p>\r\n\r\n<p><img alt=\"\" src=\"http://andongpharma.com.vn/wp-content/uploads/2017/07/ngh---.jpg\" /></p>\r\n', 'trhoacc_0205.jpg', '2019-01-09'),
(9, 'Làm giàu từ Dược liệu - Tiềm năng và cơ hội', '<p><strong>B&agrave;i to&aacute;n đặt ra</strong></p>\r\n\r\n<p>Ai đ&atilde; từng tới c&aacute;c tỉnh miền n&uacute;i ph&iacute;a Bắc như H&agrave; Giang, L&agrave;o Cai chắc đều đ&atilde; được nghe c&acirc;u chuyện về đồng b&agrave;o người Dao v&agrave; những b&agrave;i thuốc, vị thuốc qu&yacute; lưu truyền từ đời n&agrave;y qua đời kh&aacute;c. V&iacute; như b&agrave;i thuốc tắm trứ danh l&agrave;m từ h&agrave;ng chục loại c&acirc;y rừng với t&aacute;c dụng lưu th&ocirc;ng kh&iacute; huyết, loại bỏ độc tố cơ thể m&agrave; chỉ những phụ nữ người Dao mới biết đầy đủ c&ocirc;ng thức, đang được quảng c&aacute;o khắp nơi ở thị trấn du lịch Sapa. C&ugrave;ng với tốc độ c&ocirc;ng nghiệp h&oacute;a v&agrave; sự ph&aacute;t triển nhanh ch&oacute;ng của du lịch, người d&acirc;n phố thị dễ d&agrave;ng c&oacute; cơ hội tiếp cận với những c&aacute;ch chăm s&oacute;c sức khỏe d&acirc;n gian độc đ&aacute;o từ c&aacute;c v&ugrave;ng miền v&agrave; những người d&acirc;n v&ugrave;ng s&acirc;u, v&ugrave;ng xa cũng c&oacute; cơ hội kiếm th&ecirc;m thu nhập từ những tri thức truyền thống đ&oacute;.</p>\r\n\r\n<p>Thế nhưng, c&oacute; rất nhiều nguy cơ tiềm ẩn đằng sau mối quan hệ cung cầu n&agrave;y. Đ&oacute; l&agrave; t&igrave;nh trạng người d&acirc;n do thấy mối lợi trước mặt m&agrave; khai th&aacute;c tận diệt dẫn đến suy kiệt dự trữ của c&aacute;c lo&agrave;i c&acirc;y thuốc qu&yacute;, đ&oacute; l&agrave; t&igrave;nh trạng chảy m&aacute;u t&agrave;i nguy&ecirc;n khi c&aacute;c thương l&aacute;i đến t&igrave;m mua từ người d&acirc;n với gi&aacute; rẻ mạt rồi b&aacute;n qua bi&ecirc;n giới. V&agrave; xa hơn nữa, đ&oacute; l&agrave; khi những người hiện nắm giữ những tri thức truyền thống kh&ocirc;ng c&ograve;n, sẽ kh&ocirc;ng c&ograve;n ai biết đầy đủ những b&agrave;i thuốc gia truyền, kh&ocirc;ng c&ograve;n ai biết t&igrave;m v&agrave; ph&acirc;n biệt ch&iacute;nh x&aacute;c c&aacute;c c&acirc;y thuốc giữa h&agrave;ng trăm h&agrave;ng ngh&igrave;n cỏ c&acirc;y hoa l&aacute;. Như vậy th&igrave; kh&ocirc;ng chỉ t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n m&agrave; cả t&agrave;i nguy&ecirc;n tri thức của ch&uacute;ng ta sẽ bị đe dọa. Vậy đ&acirc;u l&agrave; lời giải cho b&agrave;i to&aacute;n tiềm năng dược liệu Việt?</p>\r\n\r\n<p><strong>Đi t&igrave;m lời giải</strong></p>\r\n\r\n<p>Hiểu được rằng người d&acirc;n sẽ chỉ bảo tồn t&agrave;i nguy&ecirc;n khi nguồn thu nhập của họ được đảm bảo, hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; họ c&oacute; được lợi &iacute;ch từ t&agrave;i nguy&ecirc;n đ&oacute;, ti&ecirc;u chuẩn BioTrade ra đời dựa tr&ecirc;n nguy&ecirc;n tắc khai th&aacute;c bền vững t&agrave;i nguy&ecirc;n, tạo ra c&aacute;c m&ocirc; h&igrave;nh kinh tế c&ocirc;ng bằng v&agrave; bền vững. Khi được đưa v&agrave;o thực hiện tại Việt Nam, dự &aacute;n BioTrade kết hợp c&ugrave;ng một số doanh nghiệp ti&ecirc;u biểu trong lĩnh vực đ&ocirc;ng dược để cũng ph&aacute;t triển c&aacute;c v&ugrave;ng trồng v&agrave; khai th&aacute;c dược liệu đạt ti&ecirc;u chuẩn quốc tế, trước hết l&agrave; c&aacute;c ti&ecirc;u chuẩn cho canh t&aacute;c sạch an to&agrave;n như GACP-WHO, Hữu cơ. Người n&ocirc;ng d&acirc;n tại c&aacute;c v&ugrave;ng trồng n&agrave;y được tập huấn về kĩ thuật, hỗ trợ về giống, vật tư cũng như được bao ti&ecirc;u sản phẩm khi k&iacute; kết hợp đồng cung cấp với doanh nghiệp. So với thu nhập từ trồng l&uacute;a, ng&ocirc;, thu nhập từ trồng dược liệu cao gấp 3 -4 lần khiến người n&ocirc;ng d&acirc;n y&ecirc;n t&acirc;m về đời sống, họ tự &yacute; thức được cần phải bảo tồn nguồn t&agrave;i nguy&ecirc;n, bảo vệ m&ocirc;i trường để duy tr&igrave; được thu nhập.</p>\r\n\r\n<p>Được sự hỗ trợ t&agrave;i ch&iacute;nh từ Li&ecirc;n minh ch&acirc;u &Acirc;u, BioTrade đ&atilde; hợp t&aacute;c được với 12 doanh nghiệp, chiếm 80% thị phần thị trường thảo dược trong nước. C&aacute;c doanh nghiệp n&agrave;y khi tham gia v&agrave;o dự &aacute;n, thay v&igrave; phải nhập dược liệu với nguồn cung v&agrave; chất lượng kh&ocirc;ng đảm bảo, đ&atilde; x&acirc;y dựng được v&ugrave;ng nguy&ecirc;n liệu ri&ecirc;ng ổn định cả về sản lượng v&agrave; chất lượng. Th&ecirc;m v&agrave;o đ&oacute;, dự &aacute;n cũng hỗ trợ c&aacute;c doanh nghiệp mở rộng thị trường, truyền th&ocirc;ng n&acirc;ng cao nhận thức của người ti&ecirc;u d&ugrave;ng về thuốc đ&ocirc;ng dược thuần Việt, sạch v&agrave; chuẩn h&oacute;a cũng như hỗ trợ xuất khẩu, đảm bảo đầu ra cho sản xuất.</p>\r\n\r\n<p><img alt=\"Sản xuất trà thảo dược trong nhà máy\" src=\"http://biotrade.com.vn/wp-content/uploads/2016/10/MRV_5498-300x200.jpg\" /></p>\r\n\r\n<p><em>Sản xuất tr&agrave; thảo dược trong nh&agrave; m&aacute;y</em></p>\r\n\r\n<p>Với sự t&aacute;c động của BioTrade từ đầu đến cuối chuỗi gi&aacute; trị dược liệu, người ti&ecirc;u d&ugrave;ng đ&atilde; c&oacute; th&ecirc;m lựa chọn với c&aacute;c sản phẩm chăm s&oacute;c sức khỏe tự nhi&ecirc;n v&agrave; an to&agrave;n. Số sản phẩm được sản xuất theo ti&ecirc;u chuẩn của BioTrade đ&atilde; đạt gần 70 sản phẩm. Để gi&uacute;p người ti&ecirc;u d&ugrave;ng dễ nhận biết, trong thời gian tới, c&aacute;c sản phẩm n&agrave;y sẽ được gắn nh&atilde;n BioTrade khi đưa ra thị trường, n&acirc;ng cao khả năng cạnh tranh v&agrave; kh&iacute;ch lệ c&aacute;c doanh nghiệp kh&aacute;c tham gia &aacute;p dụng. Với những t&iacute;n hiệu đ&aacute;ng mừng trong ng&agrave;nh dược liệu hiện nay, ch&uacute;ng ta c&oacute; thể lạc quan về tương lai khi Việt Nam trở th&agrave;nh một nh&agrave; cung cấp c&aacute;c sản phẩm thảo dược được quốc tế c&ocirc;ng nhận, tận dụng lợi thế sẵn c&oacute; để l&agrave;m gi&agrave;u bền vững tr&ecirc;n ch&iacute;nh t&agrave;i nguy&ecirc;n của m&igrave;nh.</p>\r\n', '141114duoclieu_6134.jpg', '2019-01-10'),
(10, ' Những thực phẩm gây sỏi thận', '<p><span style=\"font-size:12px\">Sỏi thận l&agrave; hiện tượng những vi&ecirc;n sỏi rắn nhỏ trong nước tiểu kh&ocirc;ng thể tự h&ograve;a tan v&agrave; đ&agrave;o thải ra ngo&agrave;i m&agrave; lại bị mắc kẹt trong đường tiết niệu v&agrave; k&iacute;ch thước ng&agrave;y một lớn hơn, g&acirc;y đau đớn v&agrave; ảnh hưởng nghi&ecirc;m trọng đến sức khỏe người bệnh. Chế độ ăn uống kh&ocirc;ng l&agrave;nh mạnh l&agrave; một trong những nguy&ecirc;n nh&acirc;n ch&iacute;nh khiến bạn bị mắc căn bệnh n&agrave;y. Do đ&oacute;, nếu kh&ocirc;ng muốn bị sỏi thận,&nbsp;bạn h&atilde;y cẩn trọng khi ăn những thực phẩm c&oacute; chứa nhiều chất l&agrave;m suy giảm chức năng thận hoặc l&agrave;m tăng th&ecirc;m c&aacute;c yếu tố, th&agrave;nh phần h&igrave;nh th&agrave;nh n&ecirc;n sỏi.</span></p>\r\n\r\n<h2><span style=\"font-size:12px\">Dưới đ&acirc;y l&agrave; những thực phẩm g&acirc;y bệnh Sỏi thận nếu bị lạm dụng qu&aacute; nhiều:</span></h2>\r\n\r\n<p><span style=\"font-size:12px\"><strong>1. Muối</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Muối l&agrave; loại gia vị được n&ecirc;m v&agrave;o tất cả c&aacute;c m&oacute;n ăn h&agrave;ng ng&agrave;y. Ăn qu&aacute; nhiều muối sẽ gi&uacute;p giữ nước v&agrave; h&igrave;nh th&agrave;nh sỏi thận. Cơ thể của bạn kh&ocirc;ng thể tho&aacute;t khỏi tất cả lượng natri thừa, m&agrave; kết th&uacute;c l&agrave; được lưu trữ trong hệ thống thận. Ngo&agrave;i ra, n&oacute; l&agrave;m tăng huyết &aacute;p v&agrave; g&oacute;p phần tăng c&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"\" src=\"http://www.soimattraisung.vn/wp-content/uploads/2017/06/Sea-Salt.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>2. Thịt đỏ</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Protein v&agrave; chất b&eacute;o từ động vật c&oacute; li&ecirc;n quan đến sự h&igrave;nh th&agrave;nh sỏi thận v&agrave; tổn thương thận. Thực phẩm n&agrave;y cũng c&oacute; h&agrave;m lượng acid uric v&agrave; purine cao. Khi những mức n&agrave;y qu&aacute; cao, bạn c&oacute; thể bị bệnh gout hoặc sỏi thận.<img alt=\"\" src=\"http://www.soimattraisung.vn/wp-content/uploads/2017/06/thuc-pham-gay-soi-than-2.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>3. Caffeine&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&agrave; ph&ecirc; l&agrave; m&oacute;n đồ uống nhiều người th&iacute;ch th&uacute;, c&oacute; người nghiện v&igrave; sẽ khiến bạn tỉnh t&aacute;o v&agrave;o buổi s&aacute;ng. Tuy nhi&ecirc;n, một trong những hiệu ứng của n&oacute; l&agrave; n&oacute; l&agrave;m cho thận của bạn l&agrave;m việc nhiều hơn v&agrave; l&agrave;m tăng nguy cơ bị sỏi thận. Caffeine kh&ocirc;ng chỉ c&oacute; trong c&agrave; ph&ecirc; m&agrave; c&ograve;n c&oacute; trong tr&agrave; v&agrave; soda. Những đồ uống n&agrave;y l&agrave;m tăng h&agrave;m lượng canxi trong nước tiểu v&agrave; c&oacute; thể g&acirc;y suy thận.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"\" src=\"http://www.soimattraisung.vn/wp-content/uploads/2017/06/thuc-pham-gay-soi-than-1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>4. Sữa</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Sữa l&agrave; một phần quan trọng v&agrave; rất phổ biến trong chế độ ăn uống của ch&uacute;ng ta. Những thực phẩm c&oacute; h&agrave;m lượng canxi cao v&agrave; cần thiết cho sự ph&aacute;t triển của hệ xương thường c&oacute; xu hướng kh&ocirc;ng tốt cho thận. Việc ăn qu&aacute; nhiều sữa, sữa chua hoặc pho m&aacute;t l&agrave;m tăng lượng canxi trong nước tiểu v&agrave; điều n&agrave;y l&agrave;m cho thận kh&oacute; khăn hơn khi l&agrave;m việc. N&oacute;i c&aacute;ch kh&aacute;c, n&ecirc;n bổ sung canxi trong chế độ ăn uống của bạn nhưng kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều thực phẩm gi&agrave;u canxi, bổ sung ở mức độ hợp l&yacute; để kh&ocirc;ng hại thận. V&agrave; nhớ rằng, uống nước l&agrave; c&aacute;ch tốt nhất để loại bỏ độc tố trong cơ thể v&agrave; hạn chế được nguy cơ mắc bệnh.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"\" src=\"http://www.soimattraisung.vn/wp-content/uploads/2017/06/milk_625x300_41416559876.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>5. Động vật c&oacute; vỏ</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Những người mắc chứng sỏi thận n&ecirc;n tr&aacute;nh những thực phẩm gi&agrave;u oxalat. Hợp chất hữu cơ n&agrave;y g&oacute;p phần t&iacute;ch tụ natri v&agrave; canxi trong thận. Thức ăn c&oacute; chứa axit oxalic nhất l&agrave; s&ograve; ốc, nhưng ch&uacute;ng kh&ocirc;ng đơn độc.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"\" src=\"http://www.soimattraisung.vn/wp-content/uploads/2017/06/mariscos.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>6. Đồ uống c&oacute; ga, cồn</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&aacute;c nghi&ecirc;n cứu cho thấy soda v&agrave; c&aacute;c đồ uống c&oacute; ga kh&aacute;c như nước ngọt chứa axit phosphoric l&agrave;m tăng nguy cơ ph&aacute;t triển sỏi thận. C&ograve;n c&aacute;c loại đồ uống c&oacute; cồn Kh&ocirc;ng những g&acirc;y tổn thương đến gan, rượu bia c&ograve;n g&acirc;y mất nước, l&agrave;m gi&aacute;n đoạn chức năng b&igrave;nh thường của thận v&agrave; tăng nguy cơ sỏi thận.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"\" src=\"http://www.soimattraisung.vn/wp-content/uploads/2017/06/24-0934.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>7. Chất l&agrave;m ngọt nh&acirc;n tạo</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Những người ti&ecirc;u thụ c&aacute;c sản phẩm chứa chất l&agrave;m ngọt nh&acirc;n tạo c&oacute; nguy cơ mắc sỏi thận cao v&igrave; c&aacute;c chất l&agrave;m ngọt nh&acirc;n tạo khi được sử dụng thường xuy&ecirc;n sẽ l&agrave;m suy giảm chức năng thận.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"\" src=\"http://www.soimattraisung.vn/wp-content/uploads/2017/06/chat-lam-ngot-83132.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>8. Đạm tinh luyện</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&aacute;c sản phẩm đạm tinh luyện như gạo trắng, đường, bột tinh luyện chứa nồng độ insulin cao dẫn đến nhiễm tr&ugrave;ng đường tiết niệu. Bệnh n&agrave;y c&oacute; thể dẫn đến sự h&igrave;nh th&agrave;nh của c&aacute;c vi&ecirc;n sỏi trong thận</span></p>\r\n', 'seasalt_8656.jpg', '2019-01-11');
INSERT INTO `news` (`id`, `name`, `content`, `image`, `new_date`) VALUES
(11, 'Ma trận mang tên Thực phẩm chức năng', '<p><strong>Thị trường thực phẩm chức năng ph&aacute;t triển, b&ecirc;n cạnh việc mang lại nhiều lựa chọn cho người d&acirc;n th&igrave; cũng tiềm ẩn những hiểm họa kh&oacute; lường.</strong></p>\r\n\r\n<p>Chăm s&oacute;c sức khỏe bằng thực phẩm chức năng (TPCN) đang dần trở n&ecirc;n phổ biến đặc biệt với người d&acirc;n ở c&aacute;c th&agrave;nh phố lớn. Từ năm 1999, khi TPCN bắt đầ được nhập v&agrave;o Việt Nam, đến cuối năm 2012, gần như cả ng&agrave;nh dược Việt Nam đ&atilde; tham gia v&agrave;o lĩnh vực n&agrave;y. Từ 13 cơ sở sản xuất, kinh doanh năm 2000, đến cuối 2012 con số n&agrave;y tăng l&ecirc;n th&agrave;nh hơn 1,500 cơ sở, với hơn 5,500 sản phẩm v&agrave; hơn 3,500 cơ sở với 6,800 sản phẩm năm 2013. Trong đ&oacute;, 80% sản phẩm TPCN l&agrave; nhập khẩu &ndash; 20% sản phẩm sản xuất trong nước.</p>\r\n\r\n<p><strong><em>Nhu cầu của người ti&ecirc;u d&ugrave;ng</em></strong></p>\r\n\r\n<p>Điều tra của Cục An to&agrave;n Thực phẩm Bộ Y tế cho thấy, 68.1% người d&acirc;n ở H&agrave; Nội sử dụng TPCN, gần bằng với tỉ lệ 70% tại Mỹ. Con số n&agrave;y l&agrave; 40% với người d&acirc;n ở th&agrave;nh phố Hồ Ch&iacute; Minh. Những con số n&agrave;y cho thấy nhu cầu thị trường cho những sản phẩm thực phẩm chức năng l&agrave; kh&ocirc;ng hề nhỏ. L&iacute; do dẫn đến sự tăng cao trong nhu cầu n&agrave;y c&oacute; thể kể đến sự tiến bộ trong nhận thức của người d&acirc;n về chế độ dinh dưỡng v&agrave; sức khỏe, trong đ&oacute; họ ng&agrave;y c&agrave;ng &yacute; thức r&otilde; hơn về tầm quan trọng của việc tự bảo vệ sức khỏe v&agrave; ph&ograve;ng chống bệnh tật. Th&ecirc;m v&agrave;o đ&oacute;, đời sống được cải thiện, mức sống được n&acirc;ng cao cũng cho ph&eacute;p họ chi trả cho những nhu cầu &ldquo;gia tăng&rdquo; như TPCN, thay v&igrave; chỉ đơn thuần l&agrave; những nhu cầu thiết yếu thường ng&agrave;y.</p>\r\n\r\n<p><em><strong>Thực tế thị trường</strong></em></p>\r\n\r\n<p>Khảo s&aacute;t thị trường một số sản phẩm TPCN cho thấy, TCPN c&oacute; gi&aacute; từ v&agrave;i trăm ng&agrave;n đồng cho đến v&agrave;i triệu, kh&aacute; cao so với thu nhập b&igrave;nh qu&acirc;n h&agrave;ng năm của người ti&ecirc;u d&ugrave;ng. Tuy nhi&ecirc;n, chất lượng thực sự của c&aacute;c loại TPCN mới l&agrave; điều khiến ch&uacute;ng ta phải lưu t&acirc;m.</p>\r\n\r\n<p><img alt=\"thuc_pham_chuc_nang_srwj\" src=\"http://biotrade.com.vn/wp-content/uploads/2017/03/thuc_pham_chuc_nang_srwj-300x182.jpg\" /></p>\r\n\r\n<p>Th&aacute;ng 8 năm 2016, khi kiểm tra kho h&agrave;ng của một doanh nghiệp tại Thanh Tr&igrave;, H&agrave; Nội, lực lượng chức năng đ&atilde; ph&aacute;t hiện c&oacute; hơn 20 loại thực phẩm chức năng được đ&oacute;ng g&oacute;i ho&agrave;n chỉnh v&agrave; d&aacute;n tem nhập khẩu, ghi sản phẩm ch&iacute;nh h&atilde;ng, trong đ&oacute; c&oacute; nhiều loại sản phẩm quen thuộc tr&ecirc;n thị trường. Cơ sở n&agrave;y cho biết, c&aacute;c thực phẩm chức năng n&agrave;y được nhập từ bi&ecirc;n giới ph&iacute;a Bắc, c&aacute;c thương l&aacute;i Trung Quốc đảm nhận vận chuyển h&agrave;ng đến tận nơi. Tổng số lượng sữa ong ch&uacute;a, nhau thai cừu, collagen c&ugrave;ng h&agrave;ng loạt sản phẩm c&oacute; nguồn gốc từ Trung Quốc được &ldquo;ph&ugrave; ph&eacute;p&rdquo; th&agrave;nh h&agrave;ng nhập khẩu từ Mỹ, ch&acirc;u &Acirc;u n&agrave;y l&ecirc;n tới con số khoảng 10 tấn. V&agrave; đ&acirc;y kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n những vụ l&agrave;m giả TPCN như thế n&agrave;y bị ph&aacute;t hiện.</p>\r\n\r\n<p>Nếu nh&igrave;n sang c&aacute;c k&ecirc;nh truyền th&ocirc;ng quảng c&aacute;o,ta dễ d&agrave;ng thấy được quảng c&aacute;o của c&aacute;c sản phẩm thực phẩm chức năng, thực phẩm bổ sung chăm s&oacute;c sức khỏe chiếm tỉ suất v&ocirc; c&ugrave;ng lớn. Thế nhưng, Cục An to&agrave;n Thực phẩm cũng cho biết, cứ 10 quảng c&aacute;o TPCN tr&ecirc;n truyền h&igrave;nh th&igrave; 2 quảng c&aacute;o chưa c&oacute; giấy ph&eacute;p quảng c&aacute;o, v&agrave; cứ 10 quảng c&aacute;o c&oacute; giấy ph&eacute;p th&igrave; c&oacute; 5 quảng c&aacute;o sai về nội dung so với c&ocirc;ng bố ti&ecirc;u chuẩn.</p>\r\n\r\n<p>Với một số lượng kh&ocirc;ng hề nhỏ c&aacute;c sản phẩm TPCN c&oacute; mặt tr&ecirc;n thị trường, gi&aacute; cả th&igrave; kh&ocirc;ng hề rẻ, quảng c&aacute;o cho c&aacute;c sản phẩm n&agrave;y th&igrave; xuất hiện d&agrave;y đặc trong khi chất lượng chưa được kiểm so&aacute;t chặt chẽ, người ti&ecirc;u d&ugrave;ng cần phải l&agrave;m g&igrave; để vẫn duy tr&igrave; được nhu cầu chăm s&oacute;c sức khỏe lại bảo vệ m&igrave;nh khỏi c&aacute;c nguy cơ tiềm ẩn?</p>\r\n\r\n<p><strong><em>Giải ph&aacute;p an to&agrave;n</em></strong></p>\r\n\r\n<p>Với đặc th&ugrave; phong ph&uacute; về kh&iacute; hậu, thổ nhưỡng mang lại nguồn t&agrave;i nguy&ecirc;n đa dạng sinh học dồi d&agrave;o, Việt Nam c&oacute; gần 4000 lo&agrave;i thực vật, hơn 400 lo&agrave;i động vật, 75 loại kho&aacute;ng vật v&agrave; tr&ecirc;n 50 loại tảo c&oacute; khả năng l&agrave;m thuốc. Kết hợp với nền y học cổ truyền l&acirc;u đời th&igrave; đ&acirc;y r&otilde; r&agrave;ng l&agrave; một tiềm năng v&ocirc; c&ugrave;ng to lớn cho ng&agrave;nh thực phẩm chức năng. Th&ecirc;m v&agrave;o đ&oacute;, TPCN rất dễ &aacute;p dụng c&ocirc;ng nghệ mới, nhất l&agrave; c&ocirc;ng nghệ sinh học. Việt Nam đ&atilde; nghi&ecirc;n cứu sản xuất được nhiều sản phẩm TPCN chất lượng tương đương với nước ngo&agrave;i. Sử dụng c&aacute;c sản phẩm TPCN trong nước kh&ocirc;ng chỉ gi&uacute;p khuyến kh&iacute;ch ng&agrave;nh y dược trong nước ph&aacute;t triển m&agrave; c&ograve;n hạn chế khả năng mua phải h&agrave;ng giả, h&agrave;ng nh&aacute;i theo c&aacute;c loại TPCN nhập khẩu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, c&aacute;c nh&agrave; sản xuất thuốc đ&ocirc;ng dược v&agrave; TPCN tại Việt Nam đ&atilde; đưa c&aacute;c ti&ecirc;u chuẩn quốc tế v&agrave;o &aacute;p dụng, trong đ&oacute; c&oacute; thể kể đến ti&ecirc;u chuẩn GACP-WHO cho trồng trọt v&agrave; thu h&aacute;i dược liệu, ti&ecirc;u chuẩn GMP-WHO, HACCP, ISO cho quy tr&igrave;nh sản xuất hay ti&ecirc;u chuẩn BioTrade cho thảo dược tự nhi&ecirc;n, an to&agrave;n v&agrave; sản xuất bền vững. Đ&acirc;y l&agrave; t&iacute;n hiệu t&iacute;ch cực cho thị trường bởi với lợi thế tự nhi&ecirc;n sẵn c&oacute;, việc &aacute;p dụng tiến bộ khoa học kĩ thuật sẽ gi&uacute;p tạo ra những lợi thế cạnh tranh, sau c&ugrave;ng người được hưởng lợi sẽ ch&iacute;nh l&agrave; người ti&ecirc;u d&ugrave;ng./.</p>\r\n', 'tpcn2016040515512694_4828.jpg', '2019-01-12'),
(12, 'Ung thư thực quản - 6 dấu hiệu nhận biết', '<p>Thực quản nằm ngay sau kh&iacute; quản, l&agrave; ống ti&ecirc;u h&oacute;a dẫn thức ăn v&agrave; c&aacute;c chất lỏng đi từ họng xuống dạ d&agrave;y. Thực quản của&nbsp;người trưởng th&agrave;nh trung b&igrave;nh khoảng 25 cm.</p>\r\n\r\n<p>Ung thư thực quản&nbsp;bao gồm hai dạng ch&iacute;nh l&agrave;&nbsp;<em><u>ung thư biểu m&ocirc; vảy</u></em>&nbsp;v&agrave;&nbsp;<em><u>ung thư biểu m&ocirc; tuyến</u></em>&nbsp;t&ugrave;y thuộc v&agrave;o loại tế b&agrave;o ung thư. N&oacute; cũng c&oacute; thể x&acirc;m lấn v&agrave;o&nbsp;hầu hết bộ phận kh&aacute;c của cơ thể bao gồm gan, phổi, n&atilde;o, xương...</p>\r\n\r\n<p><img alt=\"\" src=\"http://tapchiyduoc.com/images/2016/8/9/6_dau_hieu_nhan_biet_ung_thu_thuc_quan_6.jpg\" /></p>\r\n\r\n<p>Theo tư vấn của c&aacute;c b&aacute;c sĩ Bệnh viện K Trung ương (H&agrave; Nội),&nbsp;ung thư thực quản thường kh&ocirc;ng c&oacute; biểu hiện triệu chứng g&igrave; nhiều ở giai đoạn đầu. Tuy nhi&ecirc;n khi khối u ph&aacute;t triển, c&oacute; thể xuất hiện c&aacute;c dấu hiệu:</p>\r\n\r\n<p>- Nuốt đau, nuốt kh&oacute;.</p>\r\n\r\n<p>- Giảm c&acirc;n, gầy guộc</p>\r\n\r\n<p>- Đau lưng, đau họng, đau&nbsp;ph&iacute;a sau xương ức hoặc hai xương bả vai.</p>\r\n\r\n<p>- R&aacute;t họng hoặc ho k&eacute;o d&agrave;i.</p>\r\n\r\n<p>- N&ocirc;n.</p>\r\n\r\n<p>- Ho ra m&aacute;u.</p>\r\n\r\n<p>Nếu xuất hiện c&aacute;c triệu chứng n&agrave;y, bạn cần phải đến b&aacute;c sĩ kiểm tra để c&oacute; c&aacute;ch ph&ograve;ng ngừa v&agrave; ngăn chặn bệnh sớm nhất c&oacute; thể.</p>\r\n\r\n<p>Để chẩn đo&aacute;n Ung thư thực quản, b&aacute;c sĩ sẽ chỉ định bệnh nh&acirc;n thực hiện c&aacute;c x&eacute;t nghiệm như : chụp ảnh thực quản c&oacute; uống thuốc cản quang; nội soi thực quản. Nếu c&oacute; nghi vấn&nbsp;bất thường, b&aacute;c sĩ c&oacute; thể lấy bệnh phẩm tế b&agrave;o v&agrave; m&ocirc; bệnh học l&agrave;m giải phẫu bệnh. Từ đ&oacute; sẽ nh&igrave;n thấy c&aacute;c tế b&agrave;o&nbsp;ung thư hoặc c&aacute;c bất thường dẫn đến ung thư hay tổn thương kh&aacute;c.</p>\r\n\r\n<p>Để đưa ra ph&aacute;c đồ điều trị cho bệnh Ung thư thực quản cần dựa tr&ecirc;n&nbsp;phụ thuộc một số yếu tố như : k&iacute;ch thước, vị tr&iacute;, sự lan tr&agrave;n khối u v&agrave; t&igrave;nh trạng chung của bệnh nh&acirc;n. Biện ph&aacute;p chủ yếu điều trị ung thư thực quản đ&oacute; ch&iacute;nh l&agrave; phẫu thuật. Th&ocirc;ng thường khối u được mổ lấy c&ugrave;ng với một phần hoặc to&agrave;n bộ thực quản, tổ chức bạch huyết kế cận v&agrave; c&aacute;c tổ chức kh&aacute;c trong v&ugrave;ng. Phần c&ograve;n lại của thực quản sẽ được nối với dạ d&agrave;y gi&uacute;p bệnh nh&acirc;n vẫn tiếp tục nuốt như b&igrave;nh thường. Trong một số trường hợp đoạn nối c&oacute; thể được tạo bởi một đoạn ruột non hoặc một ống nhựa.&nbsp;B&ecirc;n cạnh đ&oacute;, c&ograve;n c&aacute;c phương ph&aacute;p như xạ trị, h&oacute;a trị, điều trị laser, điều trị quang động học&hellip;</p>\r\n\r\n<p>C&aacute;ch tốt nhất để&nbsp;ph&ograve;ng bệnh Ung thư thực quản&nbsp;l&agrave; từ bỏ hoặc kh&ocirc;ng bao giờ h&uacute;t thuốc l&aacute;, hạn chế uống rượu. C&aacute;c nh&agrave; nghi&ecirc;n cứu vẫn tiếp tục t&igrave;m kiếm c&aacute;c khả năng l&agrave;m giảm nguy cơ bị ung thư thực quản bằng c&aacute;ch thay đổi chế độ ăn, tăng lượng rau v&agrave; hoa quả ăn h&agrave;ng ng&agrave;y.</p>\r\n', '6dauhieunhanbietungthuthucquan6_7503.jpg', '2019-01-14');

